_a-0000ebcf-042b-8000-9d59-011c48011c48_.sail_code:
if(
  a!isNotNullOrEmpty(ri!importedClauses),
  a!localVariables(
    /*Ignoring clauses with blank values for required fields*/
    local!validImportedClauses: rule!AS_CO_UT_cast(
      typeOf: ri!importedClauses,
      value: a!flatten(
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!importedClauses,
          expression: if(
            all(
              a!isNotNullOrEmpty,
              {
                fv!item[recordType!AS GCM R Clause.clauseNumber],
                fv!item[recordType!AS GCM R Clause.refFormatId],
                fv!item[recordType!AS GCM R Clause.ucfSectionId],
                fv!item[recordType!AS GCM R Clause.refTypeId]
              }
            ),
            fv!item,
            {}
          )
        )
      )
    ),
    local!activeClauses: rule!AS_GCM_QR_getRefClause(
      fields: {
        recordType!AS GCM R Clause.additionalSections,
        recordType!AS GCM R Clause.additionalSections
      },
      returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
      executeWhen: rule!AS_CO_UT_isNotBlank(
        local!validImportedClauses[recordType!AS GCM R Clause.clauseNumberWithAlternate]
      ),
      additionalFilters: #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!AS GCM R Clause.clauseNumberWithAlternate,
        operator: "in",
        value: local!validImportedClauses[recordType!AS GCM R Clause.clauseNumberWithAlternate]
      )
    ),
    local!updatedClauses: a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: ri!importedClauses,
        expression: a!match(
          value: fv!item,
          /* checking for the last index for clauseNumber if duplicates exists */
          whenTrue: rule!AS_CO_UT_returnLastIndexOfArray(
            intersection(
              rule!AS_CO_UT_whereContains(
                values: fv!value[recordType!AS GCM R Clause.clauseNumberWithAlternate],
                array: ri!importedClauses[recordType!AS GCM R Clause.clauseNumberWithAlternate]
              ),
              rule!AS_CO_UT_whereContains(
                values: fv!value[recordType!AS GCM R Clause.deviation],
                array: ri!importedClauses[recordType!AS GCM R Clause.deviation]
              )
            )
          ) <> fv!index,
          then: {},
          whenTrue: and(
            contains(local!validImportedClauses, fv!value),
            rule!AS_CO_UT_isNotBlank(
              input: intersection(
                rule!AS_CO_UT_whereContains(
                  values: fv!value[recordType!AS GCM R Clause.clauseNumberWithAlternate],
                  array: local!activeClauses[recordType!AS GCM R Clause.clauseNumberWithAlternate]
                ),
                rule!AS_CO_UT_whereContains(
                  values: fv!value[recordType!AS GCM R Clause.deviation],
                  array: local!activeClauses[recordType!AS GCM R Clause.deviation]
                )
              )
            )
          ),
          /*if valid - Updating Excel data in existing clause record*/
          then: a!localVariables(
            local!record: rule!AS_GCM_filterRecords(
              records: local!activeClauses,
              fieldValuePairs: {
                a!map(
                  field: recordType!AS GCM R Clause.clauseNumberWithAlternate,
                  value: fv!item[recordType!AS GCM R Clause.clauseNumberWithAlternate]
                ),
                a!map(
                  field: recordType!AS GCM R Clause.deviation,
                  value: fv!item[recordType!AS GCM R Clause.deviation]
                )
              }
            ),
            rule!AS_GCM_setRecordFields(
              record: local!record,
              alwaysOverwrite: {
                a!map(
                  field: recordType!AS GCM R Clause.refFormatId,
                  value: fv!item[recordType!AS GCM R Clause.refFormatId]
                ),
                a!map(
                  field: recordType!AS GCM R Clause.ucfSectionId,
                  value: fv!item[recordType!AS GCM R Clause.ucfSectionId]
                ),
                a!map(
                  field: recordType!AS GCM R Clause.refTypeId,
                  value: fv!item[recordType!AS GCM R Clause.refTypeId]
                ),
                a!map(
                  field: recordType!AS GCM R Clause.additionalSections,
                  value: rule!AS_GCM_filterRecords(
                    records: #"SYSTEM_SYSRULES_forEach"(
                      items: fv!item[recordType!AS GCM R Clause.additionalSections],
                      expression: #"urn:appian:function:v1:a:update"(
                        data: a!defaultValue(
                          value: a!flatten(
                            rule!AS_CO_UT_rejectNullValuesFromArray(
                              rule!AS_GAM_filterCdtOrRecordDataByField(
                                cdtOrRecord: local!record[recordType!AS GCM R Clause.additionalSections],
                                field: recordType!AS GCM R Clause Section Mapping.refSection.contractFormatId,
                                value: fv!item[recordType!AS GCM R Clause Section Mapping.refSection.contractFormatId]
                              )
                            )
                          ),
                          default: recordType!AS GCM R Clause Section Mapping(
                            recordType!AS GCM R Clause Section Mapping.clauseId: local!record[recordType!AS GCM R Clause.clauseId]
                          )
                        ),
                        index: {
                          recordType!AS GCM R Clause Section Mapping.sectionId,
                          recordType!AS GCM R Clause Section Mapping.isActive
                        },
                        value: {
                          fv!item[recordType!AS GCM R Clause Section Mapping.sectionId],
