  /*Check there is at least one Clause*/
  rule!AS_CO_UT_isBlank(ri!clausesToAdd),
  "",
  rule!AS_CO_UT_distinct(
    reject(
      rule!AS_CO_UT_isBlank(_),
      {
        touniformstring(
          /*Check required fields*/
          a!localVariables(
            local!requiredFieldsTrue: rule!AS_CO_UT_filterCdtByField(
              cdt: rule!AS_GCM_BL_importClauseFromExcelFieldsList(bundle: ri!bundle),
              field: "required",
              value: true
            ),
            if(
              any(
                rule!AS_CO_UT_isBlank(_),
                a!flatten(
                  #"SYSTEM_SYSRULES_forEach"(
                    items: local!requiredFieldsTrue.field,
                    expression: rule!AS_CO_UT_indexPath(cdt: ri!clausesToAdd, field: fv!item)
                  )
                )
              ),
              {
                rule!AS_GAM_CO_I18N_UT_displayLabel(
                  bundle: ri!bundle,
                  bundleKey: "vld_FieldsRequired",
                  arguments: joinarray(local!requiredFieldsTrue.label, ", ")
                )
              },
              ""
            )
          ),
          /*Clause Duplicate Validations*/
          #"SYSTEM_SYSRULES_forEach"(
            items: ri!clausesToAdd,
            expression: {
              if(
                rule!AS_CO_UT_checkIfDuplicateExistsInArrayByIndex(
                  array: merge(
