a!localVariables(
  local!bundle: rule!AS_GCM_CO_UT_loadBundleFromFolder(langISOCode: null()),
  local!activeStep: 1,
  local!isZipImport: toboolean(null),
  /*Template workflow localvariables*/
  local!clauseImportDocument,
  local!refData: rule!AS_GCM_QR_getRefData(
    refTypes: {
      cons!AS_GCM_REF_TYPE_CLAUSE_SOURCE,
      cons!AS_GCM_REF_TYPE_CLAUSE_TYPE,
      cons!AS_GCM_REF_TYPE_FORMAT
    },
    doCastToCdt: false()
  ),
  local!refSource: rule!AS_GAM_filterCdtOrRecordDataByField(
    cdtOrRecord: local!refData,
    field: recordType!AS GCM R Data.refType,
    value: cons!AS_GCM_REF_TYPE_CLAUSE_SOURCE
  ),
  local!refType: rule!AS_GAM_filterCdtOrRecordDataByField(
    cdtOrRecord: local!refData,
    field: recordType!AS GCM R Data.refType,
    value: cons!AS_GCM_REF_TYPE_CLAUSE_TYPE
  ),
  local!refFormat: rule!AS_GAM_filterCdtOrRecordDataByField(
    cdtOrRecord: local!refData,
    field: recordType!AS GCM R Data.refType,
    value: cons!AS_GCM_REF_TYPE_FORMAT
  ),
  local!refSection: rule!AS_GCM_QR_getReferenceSectionData(executeWhen: true),
  /*Return the possible clause fields which we are mapping to*/
  local!clauseFields: rule!AS_GCM_BL_importClauseFromExcelFieldsList(bundle: local!bundle),
  /*Parse the clause import data from the Excel document*/
  local!clauseImportData: rule!AS_GCM_UT_readExcel(
    excelDocument: local!clauseImportDocument,
    numberOfColumns: count(local!clauseFields)
  ),
  /*Return an array of headers from Excel with their matching clause field*/
  local!excelColumns: #"SYSTEM_SYSRULES_forEach"(
    items: local!clauseImportData.headers,
    expression: {
      excelHeader: fv!item,
      clauseField: index(
        rule!AS_CO_UT_filterCdtByField(
          cdt: local!clauseFields,
          field: "label",
          value: fv!item
        ),
        1,
        null
      )
    }
  ),
  /*The document isn't blank and the parsing returned a valid value*/
  local!fileSuccessfullyUploaded: and(
    rule!AS_CO_UT_isNotBlank(local!clauseImportDocument),
    rule!AS_CO_UT_isNotBlank(local!clauseImportData)
  ),
  local!importedClauseData: a!refreshVariable(
    value: rule!AS_GCM_UT_mapImportExcelToClause(
      bundle: local!bundle,
      clauseImportData: local!clauseImportData,
      excelColumns: local!excelColumns,
      refFormat: local!refFormat,
      refSection: local!refSection,
      refSource: local!refSource,
      refType: local!refType
    ),
    refreshOnVarChange: { local!clauseImportData }
  ),
  local!finalImportedClauses,
  local!showValidationMessage: and(
    rule!AS_CO_UT_not(local!fileSuccessfullyUploaded),
    rule!AS_CO_UT_isNotBlank(local!clauseImportDocument),
    local!activeStep = 2,
    local!isZipImport = false
  ),
  local!isZipFailure: false(),
  /*add Zip workflow localvariables below*/
  #"SYSTEM_SYSRULES_formLayout_v1"(
    label: "",
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          /*Left Side*/
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: #"SYSTEM_SYSRULES_cardLayout"(
              contents: {
                rule!AS_GCM_CPS_headerSectionForImportClause(
                  activeStep: local!activeStep,
                  isZipImport: local!isZipImport,
                  bundle: local!bundle
                ),
                /*Sample Import File for template import*/
                #"SYSTEM_SYSRULES_richTextDisplayField"(
                  labelPosition: "COLLAPSED",
                  value: {
                    #"SYSTEM_SYSRULES_richTextIcon"(
                      icon: "download",
                      altText: "Download",
                      link: rule!AS_GAM_CP_documentDownloadLink(
                        document: cons!AS_GCM_DOC_IMPORT_CLAUSE_TEMPLATE
                      ),
                      linkStyle: "STANDALONE",
                      size: "MEDIUM"
                    ),
                    #"SYSTEM_SYSRULES_richTextItem_v1"(text: " "),
                    #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: "",
                      link: rule!AS_GAM_CP_documentDownloadLink(
                        document: cons!AS_GCM_DOC_IMPORT_CLAUSE_TEMPLATE
                      ),
                      linkStyle: "STANDALONE",
                      size: "MEDIUM",
                      style: "STRONG"
                    )
                  },
                  showWhen: and(
                    toboolean(local!isZipImport) = false,
                    local!activeStep = 2
                  ),
                  align: "LEFT",
                  marginAbove: "STANDARD"
                )
              },
              showBorder: false
            ),
            width: if(
              #"SYSTEM_SYSRULES_isPageWidth"(
                {
                  "PHONE",
                  "TABLET_PORTRAIT",
                  "TABLET_LANDSCAPE",
                  "DESKTOP_NARROW"
                }
              ),
              "MEDIUM",
              "MEDIUM_PLUS"
            ),
            showWhen: not(
              and(
                local!activeStep = 4,
                local!isZipImport = true
              )
            )
          ),
          /*BUFFER*/
          #"SYSTEM_SYSRULES_columnLayout"(
            width: "EXTRA_NARROW",
            showWhen: not(
              and(
                local!activeStep = 4,
                local!isZipImport = true
              )
            )
          ),
          /*Right Side*/
