/*This rule should not have test case since this is risk evaluation
To test this expression you can modify the cons!AS_GCM_TXT_ACQUISITION_REQUIRED_CLAUSES_COLUMN_HEADERS_FOR_SYNC 
for which this expression should return false()
*/
a!localVariables(
  local!staticData: rule!AS_GCM_DFARS_getRefTypeFromSync(source: cons!AS_GCM_REF_ID_SOURCE_FAR),
  and(
    /*Checking for "FARmatrix.dita" and "FAR.ditamap"*/
    if(
      a!isNullOrEmpty(ri!zipDocument),
      /*Use integration*/
      contains(
        touniformstring(
          rule!AS_GCM_getDitaTree(executeWhen: true(),sourceId: cons!AS_GCM_REF_ID_SOURCE_FAR).path
        ),
        local!staticData.requiredFiles
      ),
      /*Extract from zip document*/
      rule!AS_GCM_doFilesExistInZip(
        zipDocument: ri!zipDocument,
        baseFilePath: local!staticData.ditaFolderZipPath,
        fileNames: local!staticData.requiredFiles
      )
    ),
    /*Checking if the required headers are present
and then checking if the clauses have unique clause numbers
and then checking if all the columns are retrievable*/
    a!localVariables(
      local!clausesToCheck: even(
        cons!AS_GCM_INT_CLAUSES_FOR_SYNC_RISK_EVALUATION
      ),
      local!farMatrixResponse: rule!AS_GCM_getFileDataViaZipDocumentOrGitHub(
        zipDocument: ri!zipDocument,
        fileNames: local!staticData.requiredFiles[1],
        integrationResponseMaps: ri!integrationResponseMaps,
        sourceId: cons!AS_GCM_REF_ID_SOURCE_FAR
      ),
      local!farMatrixDocOrXml: if(
        toboolean(local!farMatrixResponse.success),
        if(
          a!isNotNullOrEmpty(ri!zipDocument),
          local!farMatrixResponse.result.body,
          todocument(local!farMatrixResponse.result.body)
        ),
        null
      ),
      local!columnHeaders: if(
        a!isNotNullOrEmpty(local!farMatrixDocOrXml),
        rule!AS_GCM_BL_getHeaderFromFarMatrixData(
          farMatrixDocOrXml: local!farMatrixDocOrXml
        ),
        {}
      ),
      and(
        toboolean(local!farMatrixResponse.success),
        /*Checking if local!columnHeaders contains all the column headers from the constant*/
        contains(
          local!columnHeaders,
          cons!AS_GCM_TXT_ACQUISITION_REQUIRED_CLAUSES_COLUMN_HEADERS_FOR_SYNC
        ),
        a!localVariables(
          local!clauses: a!flatten(
            #"SYSTEM_SYSRULES_forEach"(
              items: enumerate(local!clausesToCheck / 2),
              expression: {
                xpathsnippet(
                  local!farMatrixDocOrXml,
                  "//tbody/row[" & fv!index & "]"
                ),
                xpathsnippet(
                  local!farMatrixDocOrXml,
                  "//tbody/row[last()-" & fv!index - 1 & "]"
                )
              }
            )
          ),
          /*Checking if all the clauses have unique clause numbers*/
          and(
            length(
              rule!AS_CO_UT_distinct(
                value: a!flatten(
                  #"SYSTEM_SYSRULES_forEach"(
                    items: local!clauses,
                    expression: xpathsnippet(
                      fv!item,
                      "//row/entry[" & wherecontains(
                        rule!AS_GCM_getFarMatrixColumnHeadersByKeys(keys: "provisionOrClause"),
                        local!columnHeaders
                      ) & "]/p/xref/text()"
                    )
                  )
                )
              )
            ) = local!clausesToCheck,
            /*Checking if all the required columns are retrievable*/
            #"SYSTEM_SYSRULES_forEach"(
              items: local!clauses,
              expression: count(xpathsnippet(fv!item, "//row/entry")) >= count(
                cons!AS_GCM_TXT_ACQUISITION_REQUIRED_CLAUSES_COLUMN_HEADERS_FOR_SYNC
              )
            )
          )
        )
      )
    )
  )
)

