  local!staticData: rule!AS_GCM_DFARS_getRefTypeFromSync(source: cons!AS_GCM_REF_ID_SOURCE_DFARS),
  and(
    /*Checking for "PART_252.dita"*/
    if(
      a!isNullOrEmpty(ri!zipDocument),
      /*Use integration*/
      contains(
        touniformstring(
          rule!AS_GCM_getDitaTree(
            executeWhen: true(),
            sourceId: cons!AS_GCM_REF_ID_SOURCE_DFARS
          ).path
        ),
        local!staticData.requiredFiles
      ),
      /*Extract from zip document*/
      rule!AS_GCM_doFilesExistInZip(
        zipDocument: ri!zipDocument,
        baseFilePath: local!staticData.ditaFolderZipPath,
        fileNames: local!staticData.requiredFiles
      )
    ),
    /*Checking if the clauses have unique clause numbers*/
    a!localVariables(
      local!clausesToCheck: even(
        cons!AS_GCM_INT_CLAUSES_FOR_SYNC_RISK_EVALUATION
      ),
      local!response: rule!AS_GCM_getFileDataViaZipDocumentOrGitHub(
        zipDocument: ri!zipDocument,
        fileNames: local!staticData.requiredFiles[1],
        integrationResponseMaps: ri!integrationResponseMaps,
        sourceId: cons!AS_GCM_REF_ID_SOURCE_DFARS
      ),
      and(
        toboolean(local!response.success),
        a!localVariables(
          local!allClauses: rule!AS_GCM_DFARS_extractClausesFromXml(
            dfarsDocOrXml: if(
              a!isNotNullOrEmpty(ri!zipDocument),
              local!response.result.body,
              todocument(local!response.result.body)
            )
          ),
          /*Checking if all the clauses have unique clause numbers*/
          length(
            rule!AS_CO_UT_distinct(
              value: a!flatten(
                #"SYSTEM_SYSRULES_forEach"(
                  items: enumerate(local!clausesToCheck / 2),
                  expression: {
                    local!allClauses[fv!index].clauseNumber,
                    local!allClauses[length(local!allClauses) - fv!item].clauseNumber
                  }
                )
              )
            )
          ) = local!clausesToCheck
        )
      )
    )
  )
)

