a!localVariables(
  /* removing the hidden tag */
  local!removeHiddenTag: if(
    ri!ignoreHiddenTag,
    ri!text,
    rule!AS_GCM_substituteListFromTextUsingReduce(
      text: ri!text,
      /*get hidden tag */
      find: rule!AS_GCM_extractBalancedTagThatHasGivenText(
        text: ri!text,
        uniqueTextInTag: "span hidden"
      ),
      replaceWith: "",
      replaceAllInstances: true()
    )
  ),
  rule!AS_CO_UT_isNotBlank(
    /* ignore the extra space */
    trim(
      rule!AS_GCM_substituteListFromTextUsingReduce(
        text: striphtml(local!removeHiddenTag),
        /* removing the inivisible characters 
        10 - newline
        32 - space
        160 - non breakable space (&nbsp)
        */
        find: { char(10), char(32), char(160) },
        replaceWith: "",
        replaceAllInstances: true()
      )
    )
  )
)

