a!localVariables(
  local!templateNames: rule!AS_GCM_QR_getTemplate(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_OBJECT_ARRAY,
    fields: recordType!AS GCM R Template.templateName,
    additionalFilters: #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AS GCM R Template.templateId,
      operator: "<>",
      value: ri!template[recordType!AS GCM R Template.templateId],
      applyWhen: rule!AS_CO_UT_isNotBlank(
        ri!template[recordType!AS GCM R Template.templateId]
      )
    )
  )[recordType!AS GCM R Template.templateName],
  if(
    contains(
      lower(
        touniformstring(
          rule!AS_CO_UT_rejectNullValuesFromArray(touniformstring(local!templateNames))
        )
      ),
      lower(
        ri!template[recordType!AS GCM R Template.templateName]
      )
    ),
    "",
    null()
  )
)

