_a-0000e675-e3a8-8000-9bcb-011c48011c48_.sail_code:
a!localVariables(
  local!triggerRefresh,
  local!clausesToAdd: a!refreshVariable(
    value: rule!AS_CO_UT_cast(
      type: #"urn:appian:function:v1:a:listtype?okey=a!listType"(
        recordType!AS GCM R Clause
      ),
      value: {}
    ),
    refreshOnVarChange: { local!triggerRefresh, ri!triggerRefresh }
  ),
  local!clausesToRemove: a!refreshVariable(
    value: rule!AS_CO_UT_cast(
      type: #"urn:appian:function:v1:a:listtype?okey=a!listType"(
        recordType!AS GCM R Clause
      ),
      value: {}
    ),
    refreshOnVarChange: { local!triggerRefresh, ri!triggerRefresh }
  ),
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            rule!AS_GCM_QNM_GRD_availableClausesCatalog(
              availableRefClauses: local!clausesToAdd,
              bundle: ri!bundle,
              triggerRefresh: ri!triggerRefresh,
              isIncludeClausesPage: ri!isIncludeClausesPage,
              unavailableClauseIds: ri!unavailableClauseIds,
              isTemplate: ri!isTemplate
            )
          }
        ),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            #"SYSTEM_SYSRULES_forEach"(
              enumerate(30),
              if(
                fv!index < 16,
                #"SYSTEM_SYSRULES_sectionLayout_v1"(
                  showWhen: #"SYSTEM_SYSRULES_isPageWidth"(
                    {
                      "DESKTOP_NARROW",
                      "DESKTOP",
                      "DESKTOP_WIDE"
                    }
                  )
                ),
                #"SYSTEM_SYSRULES_sectionLayout_v1"(
                  showWhen: #"SYSTEM_SYSRULES_isPageWidth"(
                    {
                      "TABLET_PORTRAIT",
                      "TABLET_LANDSCAPE",
                      "DESKTOP_NARROW",
                      "DESKTOP",
                      "DESKTOP_WIDE"
                    }
                  )
                )
              )
            ),
            #"SYSTEM_SYSRULES_ButtonArrayLayout"(
              buttons: {
                #"SYSTEM_SYSRULES_buttonWidget_v1"(
                  label: rule!AS_GCM_returnValueBasedOnAutomationToggle(
                    value: ""
                  ),
                  style: "OUTLINE",
                  disabled: rule!AS_CO_UT_isBlank(local!clausesToAdd),
                  saveInto: {
                    if(
                      ri!isTemplate,
                      a!save(
                        ri!templateClauses,
                        append(
                          ri!templateClauses,
                          #"SYSTEM_SYSRULES_forEach"(
                            items: local!clausesToAdd,
                            expression: if(
                              contains(
                                tointeger(
                                  ri!templateClauses[recordType!AS GCM Template Clause.refClauseId]
                                ),
                                tointeger(
                                  fv!item[recordType!AS GCM R Clause.clauseId]
                                )
                              ),
                              {},
                              {
                                recordType!AS GCM Template Clause(
                                  recordType!AS GCM Template Clause.refClauseId: fv!item[recordType!AS GCM R Clause.clauseId],
                                  recordType!AS GCM Template Clause.refUsageId: if(
                                    ri!isIncludeClausesPage,
                                    cons!AS_GCM_REF_ID_USAGE_INCLUDED,
                                    cons!AS_GCM_REF_ID_USAGE_EXCLUDED
                                  ),
                                  recordType!AS GCM Template Clause.isActive: true
                                )
                              }
                            )
                          )
                        )
                      ),
                      a!save(
                        /* local variable hasn't been used for the reptitive "if" code because it
                        didn't disable the row selection in available grid */
                        ri!ruleClauses,
                        append(
                          ri!ruleClauses,
                          #"SYSTEM_SYSRULES_forEach"(
                            items: local!clausesToAdd,
                            expression: recordType!AS GCM QNM T Rule Condition Clause(
                              recordType!AS GCM QNM T Rule Condition Clause.refClauseId: fv!item[recordType!AS GCM R Clause.clauseId],
                              recordType!AS GCM QNM T Rule Condition Clause.refUsageId: if(
                                ri!isIncludeClausesPage,
                                cons!AS_GCM_REF_ID_USAGE_INCLUDED,
                                cons!AS_GCM_REF_ID_USAGE_EXCLUDED
                              ),
                              recordType!AS GCM QNM T Rule Condition Clause.isActive: true
                            )
                          )
                        )
                      )
                    ),
                    a!save(local!triggerRefresh, now()),
                    if(
                      ri!isTemplate,
                      rule!AS_GCM_MTR_INCLUSION_addClauseToTemplate(),
                      rule!AS_GCM_MTR_INCLUSION_addClauseToRule()
                    )
                  },
                  icon: "angle-right-bold",
                  tooltip: if(
                    rule!AS_CO_UT_isBlank(local!clausesToAdd),
                    {},
                    rule!AS_GAM_CO_I18N_UT_displayLabel(
                      bundle: ri!bundle,
                      bundleKey: if(
                        ri!isIncludeClausesPage,
                        "hlp_AddToIncluded",
                        "hlp_AddToExcludedClauses"
                      )
                    )
                  )
                )
              },
              align: "CENTER",
              marginBelow: "STANDARD"
            ),
            #"SYSTEM_SYSRULES_ButtonArrayLayout"(
              buttons: {
                #"SYSTEM_SYSRULES_buttonWidget_v1"(
                  label: rule!AS_GCM_returnValueBasedOnAutomationToggle(
                    value: ""
                  ),
                  style: "OUTLINE",
                  disabled: rule!AS_CO_UT_isBlank(local!clausesToRemove),
                  saveInto: {
                    if(
                      ri!isTemplate,
                      {
                        a!save(
                          ri!templateClauses[wherecontains(
                            tointeger(
                              local!clausesToRemove[recordType!AS GCM R Clause.clauseId]
                            ),
                            tointeger(
                              ri!templateClauses[recordType!AS GCM Template Clause.refClauseId]
                            )
                          )][recordType!AS GCM Template Clause.isActive],
                          false
                        ),
                        a!save(
                          ri!templateClauses,
                          remove(
                            ri!templateClauses,
                            rule!AS_GCM_filterRecords(
                              records: ri!templateClauses,
                              fieldValuePairs: {
                                a!map(
                                  field: recordType!AS GCM Template Clause.refClauseId,
                                  value: tointeger(
                                    local!clausesToRemove[recordType!AS GCM R Clause.clauseId]
                                  )
                                ),
                                a!map(
                                  field: recordType!AS GCM Template Clause.relationId,
                                  value: null
                                )
                              },
                              filterType: "MATCH_INDICES"
                            )
                          )
                        ),
                        rule!AS_GCM_MTR_EXCLUSION_excludeClauseFromTemplate()
                      },
                      /*A gist of #1 issue in GAM-2567: When a clause is added to exclude clauses and immediately removed back, it was not captured in the audit,But it is fixed in included clauses.
Soitishandledinthesamewayforexcludedclausestoavoidthesameissueinaudit.*/
                      {
                        a!save(
                          ri!ruleClauses[wherecontains(
                            tointeger(
                              local!clausesToRemove[recordType!AS GCM R Clause.clauseId]
                            ),
                            tointeger(
                              ri!ruleClauses[recordType!AS GCM QNM T Rule Condition Clause.refClauseId]
                            )
                          )][recordType!AS GCM QNM T Rule Condition Clause.isActive],
                          false
                        ),
                        a!save(
                          ri!ruleClauses,
                          remove(
                            ri!ruleClauses,
                            rule!AS_GCM_filterRecords(
                              records: ri!ruleClauses,
                              fieldValuePairs: {
                                a!map(
                                  field: recordType!AS GCM QNM T Rule Condition Clause.refClauseId,
                                  value: tointeger(
                                    local!clausesToRemove[recordType!AS GCM R Clause.clauseId]
                                  )
                                ),
                                a!map(
                                  field: recordType!AS GCM QNM T Rule Condition Clause.clauseMapId,
                                  value: null
                                )
                              },
                              filterType: "MATCH_INDICES"
                            )
                          )
                        ),
                        rule!AS_GCM_MTR_EXCLUSION_addClauseToRule()
                      }
                    ),
                    a!save(local!triggerRefresh, now())
                  },
                  icon: "angle-left-bold",
                  tooltip: if(
                    rule!AS_CO_UT_isBlank(local!clausesToRemove),
                    {},
                    rule!AS_GAM_CO_I18N_UT_displayLabel(
                      bundle: ri!bundle,
                      bundleKey: if(
                        ri!isIncludeClausesPage,
                        "hlp_RemoveFromIncluded",
                        "hlp_RemoveFromExcludedClauses"
                      )
                    )
                  )
                )
              },
              align: "CENTER",
              marginBelow: "STANDARD"
            )
          },
          width: "EXTRA_NARROW"
        ),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            rule!AS_GCM_QNM_GRD_includedAndExcludedClauses(
              selectedRefClauses: local!clausesToRemove,
              addedClauseIds: if(
                ri!isTemplate,
                rule!AS_GCM_filterRecords(
                  records: ri!templateClauses,
                  field: recordType!AS GCM Template Clause.isActive,
                  value: true
                )[recordType!AS GCM Template Clause.refClauseId],
                rule!AS_GCM_filterRecords(
                  records: ri!ruleClauses,
                  field: recordType!AS GCM QNM T Rule Condition Clause.isActive,
                  value: true
                )[recordType!AS GCM QNM T Rule Condition Clause.refClauseId]
              ),
              bundle: ri!bundle,
              triggerRefresh: ri!triggerRefresh,
              isIncludeClausesPage: ri!isIncludeClausesPage,
              isTemplate: ri!isTemplate
            )
          }
        )
      },
      alignVertical: "TOP",
      showWhen: ri!showWhen,
      spacing: "STANDARD",
      showDividers: false
    )
  }
)

