_a-0000eb97-b2e8-8000-9d39-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_gridColumn"(
  label: "",
  sortField: if(
    rule!AS_CO_UT_booleanDefaultFalse(ri!disableSorting),
    {},
    recordType!AS GCM R Clause.clauseSort
  ),
  value: #"SYSTEM_SYSRULES_richTextDisplayField"(
    value: #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: {
        rule!AS_GCM_CO_DSP_primaryRecordListField(
          recordTitle: a!match(
            value: rule!AS_CO_UT_booleanDefaultFalse(ri!showTruncatedClauseTitle),
            whenTrue: and(
              rule!AS_CO_UT_isNotBlank(ri!customTruncationLimit),
              len(
                fv!row[recordType!AS GCM R Clause.clauseTitle]
              ) > ri!customTruncationLimit
            ),
            then: rule!AS_GCM_DSP_clauseRecordTitle(
              clauseNumber: fv!row[recordType!AS GCM R Clause.clauseNumber],
              clauseTitle: fv!row[recordType!AS GCM R Clause.clauseTitle],
              clauseTitleLength: ri!customTruncationLimit,
              alternateValue: fv!row[recordType!AS GCM R Clause.alternateValue],
              deviation: fv!row[recordType!AS GCM R Clause.deviation]
            ),
            whenTrue: fv!value,
            then: fv!row[recordType!AS GCM R Clause.truncatedClauseTitle],
            default: fv!row[recordType!AS GCM R Clause.fullClauseTitle]
          ),
          recordType: recordType!AS GCM R Clause,
          recordIdentifier: fv!row[recordType!AS GCM R Clause.clauseId],
          linkShowWhen: ri!linkShowWhen
        ),
        if(
          or(
            rule!AS_CO_UT_booleanDefaultTrue(ri!showType),
            rule!AS_CO_UT_booleanDefaultTrue(ri!showSource),
            rule!AS_CO_UT_booleanDefaultTrue(ri!showEffectiveDate)
          ),
          {
            char(10),
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: joinarray(
                {
                  index(
                    {
                      rule!AS_GAM_CO_I18N_UT_displayLabel(
                        bundle: ri!bundle,
                        bundleKey: fv!row[recordType!AS GCM R Clause.refType.refKey]
                      ),
                      rule!AS_GAM_CO_I18N_UT_displayLabel(
                        bundle: ri!bundle,
                        bundleKey: fv!row[recordType!AS GCM R Clause.refSource.refKey]
                      ),
                      fv!row[recordType!AS GCM R Clause.effectiveDate]
                    },
                    {
                      where(
                        {
                          rule!AS_CO_UT_booleanDefaultTrue(ri!showType),
                          rule!AS_CO_UT_booleanDefaultTrue(ri!showSource),
                          rule!AS_CO_UT_booleanDefaultTrue(ri!showEffectiveDate)
                        }
                      )
                    },
                    null
                  )
                },
                " â€¢ "
              ),
              color: rule!AS_GCM_getColorBasedOnColorName(colorName: "GRAY_3"),
              size: "SMALL"
            )
          },
          {}
        )
      }
    ),
    tooltip: if(
      and(
        or(
          and(
            rule!AS_CO_UT_isNotBlank(ri!customTruncationLimit),
            len(
              fv!row[recordType!AS GCM R Clause.clauseTitle]
            ) > ri!customTruncationLimit
          ),
          and(
            rule!AS_CO_UT_booleanDefaultFalse(ri!showTruncatedClauseTitle),
            len(
              fv!row[recordType!AS GCM R Clause.clauseTitle]
            ) > cons!AS_GCM_ENUM_TEXT_LIMIT_FOR_CLAUSE_TITLE
          )
        ),
        not(
          rule!AS_CO_UT_booleanDefaultFalse(ri!preventWrapping)
        )
      ),
      fv!row[recordType!AS GCM R Clause.fullClauseTitle],
      {}
    ),
    preventWrapping: rule!AS_CO_UT_booleanDefaultFalse(ri!preventWrapping)
  ),
  align: "START",
  width: a!defaultValue(value: ri!width, default: null)
)

_a-0000e897-ddd7-8000-9c0c-011c48011c48_.sail_code:
a!localVariables(
  local!datasubset: todatasubset(
    /* Output Modifier   */
    rule!AS_GCM_updateDataByModifierMaps(
      data: ri!data,
      modifierMaps: ri!modifierMaps
    ),
    ri!pagingInfo
  ),
  a!match(
    value: ri!returnType,
    equals: cons!AS_CO_ENUM_QE_RETURN_TYPE_DATA_SUBSET,
    then: local!datasubset,
    /* When the data is null then just return the same    */
    whenTrue: rule!AS_CO_UT_isBlank(ri!data),
    then: ri!data,
    /* When the return type is SINGLE_OBJECT then type cast to its corresponding single object type   */
    equals: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    then: rule!AS_CO_UT_cast(
      typeOf: a!flatten(ri!data)[1],
      value: index(local!datasubset.data, 1, null)
    ),
    default: rule!AS_CO_UT_cast(
      typeOf: ri!data,
      value: local!datasubset.data
    )
  )
)

_a-0000e61a-f20f-8000-9ba5-011c48011c48_.sail_code:
cast(
  'type!{http://www.appian.com/ae/types/2009}Text',
