        rule!AS_CO_UT_isBlank(ri!bundleKey),
        null,
        if(
          rule!AS_CO_UT_isBlank(ri!arguments),
          rule!AS_CO_UT_replaceNull(
            nullableValue: ri!bundle[ri!bundleKey],
            replacementValue: ri!bundleKey
          ),
          rule!AS_GAM_CO_I18N_UT_replaceArguments(
            bundleKey: ri!bundle[ri!bundleKey],
            arguments: ri!arguments
          )
        )
      ),
      refreshAlways: true
    ),
    local!result
  )
)

_a-0000e897-ddd7-8000-9c0c-011c48011c48_.sail_code:
/*No assertion: As the output involves Entity DSE*/
a!localVariables(
  local!templateAudit: rule!AS_GAM_ADT_UT_buildAuditEntityData(
    auditParams: rule!AS_GCM_UT_templateStartAuditProcess_Parameters(
      originalTemplate: ri!originalTemplate,
      template: ri!template,
      originalTemplateClauses: ri!originalTemplateClauses,
      templateClauses: ri!templateClauses,
      initiator: ri!initiator,
      externalAction: ri!externalAction
    ),
    initiator: ri!initiator
  ),
  /*Audit will be written only if any of the field values are updated*/
  {
    if(
      rule!AS_CO_UT_isBlank(local!templateAudit),
      {},
      {
        entity: cons!AS_GCM_ENT_R_A_R_TEMPLATE,
        data: local!templateAudit
      }
    )
  }
)

