_a-0000e63a-a5cb-8000-9bc6-011c48011c48_.sail_code:
a!localVariables(
  local!triggerRefresh,
  local!bundle: rule!AS_GCM_CO_UT_loadBundleFromFolder(langISOCode: null()),
  /* List of steps in the milestone bar */
  local!milestoneBarSteps: {
    "Template Details",
    "Include Clauses",
    "Exclude Clauses"
  } ,
  local!activeStep: 1,
  local!refData: rule!AS_GCM_QR_getRefData(
    refTypes: {
      cons!AS_GCM_REF_TYPE_CLAUSE_TYPE,
      cons!AS_GCM_REF_TYPE_CLAUSE_SOURCE
    },
    refDataId: {
      cons!AS_GCM_REF_ID_USAGE_INCLUDED,
      cons!AS_GCM_REF_ID_USAGE_EXCLUDED
    },
    doCastToCdt: false()
  ),
  local!includedRefUsage: rule!AS_GCM_filterRecords(
    records: local!refData,
    field: recordType!AS GCM R Data.refDataId,
    value: cons!AS_GCM_REF_ID_USAGE_INCLUDED
  )[1],
  local!queriedTemplateClauses: rule!AS_GCM_QR_getTemplateClauseDetailsWithBatching(
    templateIds: ri!template[recordType!AS GCM R Template.templateId],
    executeWhen: a!isNotNullOrEmpty(
      ri!template[recordType!AS GCM R Template.templateId]
    ),
    batchingSize: cons!AS_GCM_INT_QUERY_BATCH_SIZE_LARGE,
    showInActive: false(),
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: - 1)
  ),
  /*Defaulting the old templateClauses refUsage as included*/
  local!templateClauses: rule!AS_CO_UT_cast(
    type: #"urn:appian:function:v1:a:listtype?okey=a!listType"(
      recordType!AS GCM Template Clause
    ),
    value: if(
      and(
        rule!AS_CO_UT_isNotBlank(input: local!queriedTemplateClauses),
        rule!AS_CO_UT_isBlank(
          value: local!queriedTemplateClauses[recordType!AS GCM Template Clause.refUsageId]
        )
      ),
      rule!AS_GCM_setRecordFields(
        record: local!queriedTemplateClauses,
        alwaysOverwrite: a!map(
          field: recordType!AS GCM Template Clause.refUsageId,
          value: index(
            local!includedRefUsage,
            recordType!AS GCM R Data.refDataId,
            null
          )
        )
      ),
      local!queriedTemplateClauses
    )
  ),
  local!includedTemplateClauses:
    rule!AS_GAM_filterCdtOrRecordDataByField(
      cdtOrRecord: local!templateClauses,
      field: recordType!AS GCM Template Clause.refUsageId,
      value: cons!AS_GCM_REF_ID_USAGE_INCLUDED
    ),

  local!excludedTemplateClauses:
    rule!AS_GCM_filterRecords(
      records: local!templateClauses,
      field: recordType!AS GCM Template Clause.refUsageId,
      value: cons!AS_GCM_REF_ID_USAGE_EXCLUDED
    ),
    local!unavailableClauses: rule!AS_GCM_filterRecords(
      records: append(
        local!includedTemplateClauses,
        local!excludedTemplateClauses
      ),
      field: recordType!AS GCM Template Clause.isActive,
      value: true
    ),
    #"SYSTEM_SYSRULES_formLayout_v1"(
    label: if(
      ri!isEdit = true(),
      rule!AS_GAM_CO_I18N_UT_displayLabel(
        bundle: local!bundle,
        bundleKey: "lbl_EditTemplate",
        arguments: ri!template[recordType!AS GCM R Template.templateName]
      ),
      ""
    ),
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              rule!AS_GCM_CPS_milestone(
                stepChoiceLabels: local!milestoneBarSteps,
                activeStep: local!activeStep,
                bundle: local!bundle
              )
            }
          )
        }
      ),
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              /* Wizard Steps */
              choose(
                local!activeStep,
                /*template details*/
                rule!AS_GCM_CPS_templateDetails(
                  template: ri!template,
                  bundle: local!bundle
                ),
                /*Include Clauses*/
                rule!AS_GCM_QNM_CPS_includeAndExcludeClausesForRuleAndTemplate(
                  bundle: local!bundle,
                  templateClauses: local!includedTemplateClauses,
                  unavailableClauseIds: local!unavailableClauses[recordType!AS GCM Template Clause.refClauseId],
                  triggerRefresh: local!triggerRefresh,
                  isIncludeClausesPage: true(),
                  isTemplate: true
                ),
                /*Exclude Clauses*/
                rule!AS_GCM_QNM_CPS_includeAndExcludeClausesForRuleAndTemplate(
                  bundle: local!bundle,
                  templateClauses: local!excludedTemplateClauses,
                  unavailableClauseIds: local!unavailableClauses[recordType!AS GCM Template Clause.refClauseId],
                  triggerRefresh: local!triggerRefresh,
                  isTemplate: true,
                  isIncludeClausesPage: false()
                ),
                {}
              )
            }
          )
        }
      ),

    },
    buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons: {
        #"SYSTEM_SYSRULES_buttonWidget_v1"(
          label: if(
            ri!isEdit = true(),
            "",
            ""
          ),
          submit: true,
          style: "SOLID",
          value: cons!AS_CO_ENUM_USER_ACTION_SUBMIT,
          saveInto: {
            ri!userAction,
            a!save(
              ri!template[recordType!AS GCM R Template.templateClauses],
              {
                /*New Included clauses*/
                rule!AS_GAM_filterCdtOrRecordDataByField(
                  cdtOrRecord: local!includedTemplateClauses,
                  field: recordType!AS GCM Template Clause.refClauseId,
                  value: difference(
                    tointeger(
                      index(
                        local!includedTemplateClauses,
                        recordType!AS GCM Template Clause.refClauseId,
                        null
                      )
                    ),
                    tointeger(
                      rule!AS_GAM_filterCdtOrRecordDataByField(
                        cdtOrRecord: local!templateClauses,
                        field: recordType!AS GCM Template Clause.refUsageId,
                        value: cons!AS_GCM_REF_ID_USAGE_INCLUDED
                      )[recordType!AS GCM Template Clause.refClauseId],

                    )
                  )
                ),
                /*New Excluded clauses*/
                rule!AS_GAM_filterCdtOrRecordDataByField(
                  cdtOrRecord: local!excludedTemplateClauses,
                  field: recordType!AS GCM Template Clause.refClauseId,
                  value: difference(
                    tointeger(
                      index(
                        local!excludedTemplateClauses,
                        recordType!AS GCM Template Clause.refClauseId,
                        null
                      )
                    ),
                    tointeger(
                      rule!AS_GAM_filterCdtOrRecordDataByField(
                        cdtOrRecord: local!templateClauses,
                        field: recordType!AS GCM Template Clause.refUsageId,
                        value: cons!AS_GCM_REF_ID_USAGE_EXCLUDED
                      )[recordType!AS GCM Template Clause.refClauseId],

                    )
                  )
                ),
                /*Removed Clauses*/
                rule!AS_GAM_filterCdtOrRecordDataByField(
                  cdtOrRecord: {
                    local!includedTemplateClauses,
                    local!excludedTemplateClauses
                  },
                  field: recordType!AS GCM Template Clause.isActive,
                  value: false()
                )
              }
            ),
            /*Application metrics tracking - Template Creation/Edit*/
            if(
              ri!isEdit = true(),
              rule!AS_GCM_MTR_SAVE_editTemplate(),
              rule!AS_GCM_MTR_SAVE_createTemplate()
            )
          },
          accessibilityText: if(
            ri!isEdit = true(),
            "",
            ""
          ),
          showWhen: local!activeStep = 3
        ),
        #"SYSTEM_SYSRULES_buttonWidget_v1"(
          label: "Next",
          submit: false,
          style: "SOLID",
          validate: true,
          value: cons!AS_CO_ENUM_USER_ACTION_NEXT,
          saveInto: {
            ri!userAction,
            rule!AS_CO_UT_triggerRefresh(triggerRefresh: local!triggerRefresh),
            a!save(local!activeStep, local!activeStep + 1)
          },
          showWhen: local!activeStep <> length(local!milestoneBarSteps)
        )
      },
      secondaryButtons: {
        #"SYSTEM_SYSRULES_buttonWidget_v1"(
          label: "Back",
          value: false(),
          saveInto: {
            a!save(local!activeStep, local!activeStep - 1)
          },
          submit: false,
          showWhen: local!activeStep <> 1,
          validate: false
        ),
        #"SYSTEM_SYSRULES_buttonWidget_v1"(
          label: "Cancel",
          value: cons!AS_CO_ENUM_USER_ACTION_CANCEL,
          saveInto: { ri!userAction },
          submit: true,
          style: "LINK",
          validate: false
        )
      }
    ),
    skipAutoFocus: true
