sail_code:
#"SYSTEM_SYSRULES_gridLayout"(
  labelPosition: "COLLAPSED",
  headerCells: {
    #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
      label: ""
    ),
    #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(
      label: "",
      showWhen: rule!AS_CO_UT_not(ri!preventModification)
    )
  },
  columnConfigs: {
    #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(),
    #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(
      width: "ICON",
      showWhen: rule!AS_CO_UT_not(ri!preventModification)
    )
  },
  rows: #"SYSTEM_SYSRULES_forEach"(
    items: ri!question[recordType!AS GCM QNM R Question.defaultResponseOptions],
    expression: #"SYSTEM_SYSRULES_gridRowLayout"(
      id: fv!index,
      contents: {
        rule!AS_GCM_DPL_CO_INP_textField(
          required: true,
          label: "",
          readOnly: false,
          disabled: ri!preventModification,
          value: fv!item[recordType!AS GCM QNM R Response.label],
          validations: if(
            rule!AS_CO_UT_checkIfDuplicateExistsInArrayByIndex(
              array: ri!question[recordType!AS GCM QNM R Question.defaultResponseOptions.value],
              index: fv!index,
              skipNulls: true,
              caseSensitive: false
            ),
            "",
            ""
          ),
          saveInto: a!save(
            {
              fv!item[recordType!AS GCM QNM R Response.label],
              fv!item[recordType!AS GCM QNM R Response.value]
            },
            save!value
          ),
          characterLimit: cons!AS_CO_ENUM_TEXT_LENGTH_LONG
        ),
        #"SYSTEM_SYSRULES_richTextDisplayField"(
          value: #"SYSTEM_SYSRULES_richTextIcon"(
            icon: "trash",
            altText: concat(
              "Remove",
              char(32),
              fv!item[recordType!AS GCM QNM R Response.label]
            ),
            link: #"SYSTEM_SYSRULES_dynamicLink"(
              saveInto: a!save(
                ri!question[recordType!AS GCM QNM R Question.defaultResponseOptions],
                remove(
                  ri!question[recordType!AS GCM QNM R Question.defaultResponseOptions],
                  fv!index
                )
              ),
              showWhen: rule!AS_CO_UT_not(ri!preventModification)
            ),
            linkStyle: "STANDALONE",
            size: "MEDIUM"
          ),
          showWhen: rule!AS_CO_UT_not(ri!preventModification)
        )
      }
    )
  ),
  addRowLink: #"SYSTEM_SYSRULES_dynamicLink"(
    label: "",
    saveInto: {
      a!save(
        ri!question[recordType!AS GCM QNM R Question.defaultResponseOptions],
        append(
          ri!question[recordType!AS GCM QNM R Question.defaultResponseOptions],
          recordType!AS GCM QNM R Response(
            recordType!AS GCM QNM R Response.fieldKey: "",
            recordType!AS GCM QNM R Response.label: "",
            recordType!AS GCM QNM R Response.isActive: true()
          )
        )
      )
    },
    showWhen: rule!AS_CO_UT_not(ri!preventModification)
  )
)
