sail_code:
cast(
  #"urn:appian:function:v1:a:listtype?okey=a!listType"(
    recordType!AS GCM QNM T Question
  ),
  a!localVariables(
    /* Filter first for efficiency */
    local!potentialFollowupQuestions: rule!AS_GAM_filterCdtOrRecordDataByField(
      cdtOrRecord: ri!allQuestions,
      field: recordType!AS GCM QNM T Question.questionCategoryTemplateId,
      value: ri!question[recordType!AS GCM QNM T Question.questionCategoryTemplateId]
    ),
    #"SYSTEM_SYSRULES_forEach"(
      items: local!potentialFollowupQuestions,
      expression: if(
        rule!AS_CO_UT_isBlank(
          rule!AS_GAM_filterCdtOrRecordDataByField(
            cdtOrRecord: #"SYSTEM_SYSRULES_forEach"(
              items: fv!item[recordType!AS GCM QNM T Question.isDisplayedPrecedentSets],
              expression: fv!item[recordType!AS GCM QNM T Question Precedent Set.questionPrecedents]
            ),
            field: recordType!AS GCM QNM T Question Precedent.questionTemplateId_precedent,
            value: ri!question[recordType!AS GCM QNM T Question.questionTemplateId]
          )
        ),
        {},
        fv!item
      )
    )
  )
)
