sail_code:
cast(
  'type!{http://www.appian.com/ae/types/2009}Text',
  if(
    rule!AS_CO_UT_isBlank(
      ri!currencyValue
    ),
    if(
      ri!showHyphen,
      "-",
      ""
    ),
    with(
      local!allCurrencies: rule!AS_CO_CONS_CURRENCY_REFERENCE(),
      local!currencyCode: rule!AS_CO_UT_replaceNull(
        nullableValue: ri!currencyCode,
        replacementValue: cons!AS_CO_TXT_DEFAULT_CURRENCY_CODE
      ),
      local!symbol: displayvalue(
        local!currencyCode,
        local!allCurrencies.currencyCode,
        local!allCurrencies.symbol,
        null
      ),
      local!currencyDisplay: text(
        ri!currencyValue,
        if(
          ri!showDecimal,
          "#,###,###,###,###,###,###,###,###,###,###,###,###,###,###,###,###,###,###,###.00",
          "#,###,###,###,###,###,###,###,###,###,###,###,###,###,###,###,###,###,###,###"
        )
      ),
      concat(
        local!symbol,
        local!currencyDisplay,
        if(
          ri!showCurrencyCode,
          concat(
            " ",
            local!currencyCode
          ),
          ""
        )
      )
    )
  )
)
