sail_code:
#"SYSTEM_SYSRULES_richTextDisplayField"(
  labelPosition: rule!AS_CO_UT_replaceNull(nullableValue: ri!labelPosition,replacementValue: "COLLAPSED"),
  align: ri!align,
  value: #"SYSTEM_SYSRULES_richTextHeader"(
    text: {
      if(
        rule!AS_CO_UT_isBlank(ri!headerIcon),
        "",
        {
          #"SYSTEM_SYSRULES_richTextIcon"(
            icon: ri!headerIcon,
            caption: ri!iconCaption
          ),
          " "
        }
      ),
      ri!headerText
    },
    size: rule!AS_CO_UT_displayValue(
      value: ri!headerLevel,
      inArray: rule!AS_CO_CONS_HEADER_LEVELS(),
      replacement: {
        "LARGE",
        "MEDIUM",
        "SMALL"
      },
      firstIsDefault: true
    )
  ),
  showWhen: ri!showWhen
)
