  rule!AS_CO_UT_booleanDefaultTrue(ri!showWhen),
  {
    #"SYSTEM_SYSRULES_sectionLayout_v1"(
      divider: "BELOW",
      marginBelow: "STANDARD",
      contents: {
        rule!AS_CO_DSP_displayHeader(
          headerText: upper(ri!sectionLabel),
          headerLevel: rule!AS_CO_UT_replaceNull(nullableValue: ri!headerLevel, replacementValue: cons!AS_CO_ENUM_HEADER_LEVEL_TWO),
          align: "LEFT",
          headerIcon: ri!sectionIcon,
          iconCaption: ri!iconCaption
        )
      }
    ),
    ri!contents,
    #"SYSTEM_SYSRULES_richTextDisplayField"(
      labelPosition: "COLLAPSED"
    )
  },
  {}
)
