sail_code:
tostring(
  if(
    or(
      rule!AS_CO_UT_isBlank(
        ri!data
      ),
      rule!AS_CO_UT_isBlank(
        ri!value
      )
    ),
    null,
    a!localVariables(
      local!singleData: rule!AS_CO_UT_filterCdtByField(
        cdt: ri!data,
        field: ri!valueField,
        value: ri!value
      ),
      rule!AS_CO_UT_displayRefDataLabelSingle(
        singleData: local!singleData,
        labelField: ri!labelField,
        skipInactiveAppend: ri!skipInactiveAppend,
        i18nData: ri!i18nData
      )
    )
  )
)
