sail_code:
cast(
   'type!{http://www.appian.com/ae/types/2009}Datetime?list',
  if(
    or(
      rule!AS_CO_UT_isBlank(ri!startDate),
      rule!AS_CO_UT_isBlank(ri!endDate)
    ),
    {},
    with(
      local!endDateToUse: ri!endDate + 1, 
      {
        rule!AS_CO_UT_convertDateToDateTime(date: ri!startDate, userHour: 0, userMinute: 0, userSecond: 0),
        rule!AS_CO_UT_convertDateToDateTime(date: local!endDateToUse, userHour: 0, userMinute: 0, userSecond: 0)
      }
    )
  )
)
