sail_code:
tostring(
  if(
    or(
      rule!AS_CO_UT_isBlank(ri!singleData),
      rule!AS_CO_UT_isBlank(ri!singleData[ri!labelField])
    ),
    null,
    a!localVariables(

      local!isInactive: not(rule!AS_CO_UT_booleanDefaultTrue(index(ri!singleData, "isActive", null))),

      local!rawLabel: ri!singleData[ri!labelField],

      /*If the label includes a ".", then it is likely an internationalized label, */
      /*in which case we want to run it through AS_CO_I18N_UT_displayLabel*/

      /* Right now, GAM doesn't support internationalization, so ignoring this */
      /*local!isInternationalizedLabel: count(split(local!rawLabel, "_")) > 1,

      local!displayLabel: if(
        local!isInternationalizedLabel,
        rule!AS_CO_I18N_UT_displayLabel(
          i18nData: ri!i18nData,
          bundleKey: ri!singleData[ri!labelField]
        ),
        local!rawLabel
      ),

      local!isInternationalizedLabel*/

      if(
        and(local!isInactive, ri!skipInactiveAppend <> true),
        rule!AS_CO_UT_displayCommonObjectBundleLabel(
          bundleKey: "AS.CO.CommonObjects.lbl_inactiveRefData",
          arguments: local!rawLabel
        ),
        rule!AS_CO_I18N_UT_displayLabel(
          i18nData: ri!i18nData,
          bundleKey: local!rawLabel
        )
      )
    )
  )
)
