sail_code:
a!localVariables(
  local!valueToBeDisplayedInCaption: {
    /*DOCUPLOADTYPE removed*/
    /* Max responses requirement */
    a!localVariables(
      local!maxResponses: rule!AS_CO_UT_filterCdtByField(
        cdt: ri!question.responseRequirements,
        field: "requirementTypeCode",
        value: cons!AS_GCM_QNM_ENUM_RESPONSE_REQUIREMENT_TYPE_CODE_MAX_RESPONSES
      ).requirementValue,
      if(
        rule!AS_CO_UT_isBlank(local!maxResponses),
        null,
        rule!AS_GAM_CO_I18N_UT_displayLabel(
          bundle: ri!bundle,
          bundleKey: "hlp_MaxResponseOptionToSelect",
          arguments: local!maxResponses
        )
      )
    ),
    /* Min responses requirement */
    a!localVariables(
      local!minResponses: rule!AS_CO_UT_filterCdtByField(
        cdt: ri!question.responseRequirements,
        field: "requirementTypeCode",
        value: cons!AS_GCM_QNM_ENUM_RESPONSE_REQUIREMENT_TYPE_CODE_MIN_RESPONSES
      ).requirementValue,
      if(
        rule!AS_CO_UT_isBlank(local!minResponses),
        null,
        rule!AS_GAM_CO_I18N_UT_displayLabel(
          bundle: ri!bundle,
          bundleKey: "hlp_MinResponseOptionToSelect",
          arguments: local!minResponses
        )
      )
    )
  },
  rule!AS_GCM_QNM_CPS_dropdownField(
    validations: rule!AS_GCM_QNM_VD_evaluateResponseRequirementsForField(
      question: ri!question,
      questionMetadata: ri!questionMetadata,
      fieldKey: null,
      returnValidationMessage: true
    ),
    question: ri!question,
    fieldKey: null,
    label: ri!question.label,
    disableSaves: ri!disableSaves,
    labelPosition: "ABOVE",
    helpTooltip: rule!AS_CO_UT_rejectNullValuesFromArray(local!valueToBeDisplayedInCaption)
  )
)
