sail_code:
a!localVariables(
  local!responseLabels: rule!AS_GCM_QNM_UT_filterToResponsesForQuestionByFieldKey(
    question: ri!question,
    fieldKey: null
  ).label,
  if(
    rule!AS_CO_UT_isBlank(
      local!responseLabels
    ),
    {},
    #"SYSTEM_SYSRULES_forEach"(
      items: local!responseLabels,
      expression: {
        rule!AS_GCM_QNM_UI_displayInternationalizedReadOnlySimpleTextResponse(
          /* In case the selected label is internationalized */
          text: fv!item,
          bundle: ri!i18nData
        ),
        if(
          fv!isLast,
          {},
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: char(
              10
            )
          )
        )
      }
    )
  )
)
