sail_code:
a!localVariables(
  local!responsesForOtherFields: rule!AS_CO_UT_rejectCdtByField(
    cdt: ri!question.responses,
    field: "fieldKey",
    value: ri!fieldKey
  ),
  rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
    cdt: ri!question,
    fieldValuePairs: {
      field: "responses",
      value: append(
        local!responsesForOtherFields,
        ri!currentFieldResponses
      )
    }
  )
)
