sail_code:
rule!AS_GCM_QNM_UT_buildResponseForQuestion(
  fieldKey: ri!fieldKey,
  labelValuePairs: #"SYSTEM_SYSRULES_forEach"(
    items: rule!AS_CO_UT_filterCdtByField(
      cdt: rule!AS_CO_UT_filterCdtByField(
        cdt: ri!responseOptions,
        field: "fieldKey",
        value: ri!fieldKey
      ),
      field: "value",
      value: ri!selectedValues
    ),
    expression: {
      label: fv!item.label,
      value: fv!item.value
    }
  )
)
