sail_code:
a!localVariables(
  local!result: length(ri!responses) >= tointeger(
    ri!responseRequirement.requirementValue
  ),
  if(
    ri!returnValidationMessage,
    if(
      local!result,
      "",
      rule!AS_GCM_UT_displayDynamicLabel(
        bundleKey: if(
          tointeger(
            ri!responseRequirement.requirementValue
          ) = 1,
          "vld_MinResponseValidationSingle",
          "vld_MinResponseValidationMultiple"
        ),
        arguments: tointeger(
          ri!responseRequirement.requirementValue
        )
      )
    ),
    local!result
  )
)
