sail_code:
if(
  ri!returnValidationMessage,
  rule!AS_CO_VD_textValidation(
    /* Should only be one response for a character based validation */
    value: ri!responses.value,
    minLength: ri!responseRequirement.requirementValue
  ),
  len(ri!responses.value) >= tointeger(
    ri!responseRequirement.requirementValue
  )
)
