sail_code:
if(rule!AS_CO_UT_isBlank(ri!mainFolderId),
null,
a!localVariables(
  local!parentSubfolders: folder(
    ri!mainFolderId,
    "folderChildren"
  ),
  local!subFolderIds: a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: local!parentSubfolders,
      expression: folder(
        fv!item,
        "folderChildren"
      )
    )
  ),
  {
    folderIds: local!subFolderIds,
    folderNames: a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!subFolderIds,
        expression: rule!AS_GAM_UT_concatParentFolderAndSubFolderNames(folderId: fv!item)
      )
    )
  }
))
