sail_code:
a!localVariables(
  local!isDisplayedPrecedentSets: ri!selectedQuestion[recordType!AS GCM QNM T Question.isDisplayedPrecedentSets],
  local!iterationLevel: rule!AS_CO_UT_replaceNull(
    nullableValue: ri!iterationLevel,
    replacementValue: 1
  ),
  if(
    rule!AS_CO_UT_isBlank(local!isDisplayedPrecedentSets),
    local!iterationLevel,
    #"SYSTEM_SYSRULES_forEach"(
      items: local!isDisplayedPrecedentSets,
      expression: rule!AS_GCM_QNM_UT_calculateQuestionPrecedentsDepth(
        selectedQuestion: rule!AS_GAM_filterCdtOrRecordDataByField(
          cdtOrRecord: ri!allQuestions,
          field: recordType!AS GCM QNM T Question.questionTemplateId,
          value: fv!item[recordType!AS GCM QNM T Question Precedent Set.questionPrecedents.questionTemplateId_precedent]
        ),
        iterationLevel: local!iterationLevel + 1,
        allQuestions: ri!allQuestions
      )
    )
  )
)
