sail_code:
updatearray(
  ri!allQuestions,
  rule!AS_CO_UT_replaceNull(
    nullableValue: index(
      wherecontains(
        tointeger(
          ri!question[recordType!AS GCM QNM T Question.questionTemplateId]
        ),
        tointeger(
          ri!allQuestions[recordType!AS GCM QNM T Question.questionTemplateId]
        )
      ),
      1,
      null
    ),
    replacementValue: length(ri!allQuestions) + 1
  ),
  ri!question
)
