sail_code:
a!localVariables(
  local!values: if(
    a!isNullOrEmpty(
      ri!ruleCondition[recordType!AS GCM QNM T Rule Condition.value]
    ),
    null,
    #"SYSTEM_SYSRULES_fromJson_v1"(
      ri!ruleCondition[recordType!AS GCM QNM T Rule Condition.value]
    )
  ),
  if(
    ri!readOnly,
    a!localVariables(
      local!contractTypeValues: rule!AS_CO_UT_filterCdtByField(
        cdt: ri!refData,
        field: "contractTypeId",
        value: local!values
      ),
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        labelPosition: "COLLAPSED",
        value: rule!AS_GCM_UI_showMoreOrShowLessLinkForArray(
          item: #"SYSTEM_SYSRULES_forEach"(
            items: local!contractTypeValues,
            expression: concat(
              {
                fv!item.contractCode,
                " | ",
                fv!item.contractTitle
              }
            )
          ),
          showMoreText: "Show More",
          showLessText: "Show Less",
          displayLinkAboveContent: true(),
          isBulletedList: false(),
          showLinkInNewLine: true(),
          shortenIfLongerThanCount: cons!AS_GCM_INT_LIMIT_FOR_SHOW_MORE_OR_LESS_MEDIUM_PLUS,
          linkStyle: "INLINE",
          linkTextStyle: "STRONG"
        )
      )
    ),
    rule!AS_CO_CP_multipleDropdownField(
      required: true,
      placeholderLabel: "Choose contract type(s)",
      choiceLabels: #"SYSTEM_SYSRULES_forEach"(
        items: ri!refData,
        expression: concat(
          {
            fv!item.contractCode,
            " | ",
            fv!item.contractTitle
          }
        )
      ),
      choiceValues: ri!refData.contractTypeId,
      readOnly: ri!readOnly,
      value: local!values,
      saveInto: {
        local!values,
        a!save(
          ri!ruleCondition[recordType!AS GCM QNM T Rule Condition.value],
          #"SYSTEM_SYSRULES_toJson_v1"(save!value)
        ),
        ri!additionalSaves
      },
      label: "Value",
      labelPosition: "ABOVE"
    )
  )
)
