sail_code:
a!localVariables(
  local!value: if(
    a!isNullOrEmpty(
      ri!ruleCondition[recordType!AS GCM QNM T Rule Condition.value]
    ),
    null,
    #"SYSTEM_SYSRULES_fromJson_v1"(
      ri!ruleCondition[recordType!AS GCM QNM T Rule Condition.value]
    )
  ),
  local!compValue: if(
    a!isNullOrEmpty(
      ri!ruleCondition[recordType!AS GCM QNM T Rule Condition.compValue]
    ),
    null,
    #"SYSTEM_SYSRULES_fromJson_v1"(
      ri!ruleCondition[recordType!AS GCM QNM T Rule Condition.compValue]
    )
  ),
  a!match(
    value: a!defaultValue(value: ri!readOnly, default: false),
    whenTrue: and(
      fv!value,
      tointeger(
        ri!ruleCondition[recordType!AS GCM QNM T Rule Condition.refOperatorId]
      ) = cons!AS_GCM_REF_ID_IS_BETWEEN_OPERATOR
    ),
    then: #"SYSTEM_SYSRULES_richTextDisplayField"(
      labelPosition: "COLLAPSED",
      value: {
        rule!AS_CO_UT_formatDate(local!value),
        " ",
        "",
        " ",
        rule!AS_CO_UT_formatDate(local!compValue)
      }
    ),
    whenTrue: tointeger(
      ri!ruleCondition[recordType!AS GCM QNM T Rule Condition.refOperatorId]
    ) = cons!AS_GCM_REF_ID_IS_BETWEEN_OPERATOR,
    then: #"SYSTEM_SYSRULES_sideBySideLayout"(
      items: {
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: rule!AS_CO_INP_dateField(
            required: true,
            value: local!value,
            saveInto: {
              local!value,
              a!save(
                ri!ruleCondition[recordType!AS GCM QNM T Rule Condition.value],
                #"SYSTEM_SYSRULES_toJson_v1"({ save!value })
              ),
              ri!additionalSaves
            },
            label: "Value",
            labelPosition: if(ri!readOnly, "COLLAPSED", "ABOVE"),
            readOnly: ri!readOnly
          ),
          width: "MINIMIZE"
        ),
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"SYSTEM_SYSRULES_richTextDisplayField"(
            marginAbove: "LESS",
            value: {
              char(10),
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: ""
              )
            }
          ),
          width: "MINIMIZE"
        ),
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: rule!AS_CO_INP_dateField(
            required: true,
            value: local!compValue,
            saveInto: {
              local!compValue,
              a!save(
                ri!ruleCondition[recordType!AS GCM QNM T Rule Condition.compValue],
                #"SYSTEM_SYSRULES_toJson_v1"({ save!value })
              ),
              ri!additionalSaves
            },
            label: "Compare Value",
            labelPosition: if(ri!readOnly, "COLLAPSED", "ABOVE"),
            readOnly: ri!readOnly,
            validations: if(
              and(
                rule!AS_CO_UT_isNotBlank(local!value),
                rule!AS_CO_UT_isNotBlank(local!compValue),
                local!value >= local!compValue
              ),
              "End date should be greater than start date",
              ""
            )
          ),
          width: "MINIMIZE"
        )
      },
      showWhen: not(
        a!defaultValue(value: ri!readOnly, default: false)
      ),

    ),
    default: rule!AS_CO_INP_dateField(
      required: true,
      value: local!value,
      saveInto: {
        local!value,
        a!save(
          ri!ruleCondition[recordType!AS GCM QNM T Rule Condition.value],
          #"SYSTEM_SYSRULES_toJson_v1"({ save!value })
        ),
        ri!additionalSaves
      },
      label: "Value",
      labelPosition: if(ri!readOnly, "COLLAPSED", "ABOVE"),
      readOnly: ri!readOnly
    )
  )
)
