a!localVariables(
  local!clauseSetToCopy: rule!AS_GCM_QE_getClauseSetDetails(
    returnType: cons!AS_CO_ENUM_QE_RETURN_TYPE_SINGLE_OBJECT,
    awardId: tointeger(
      index(
        ri!clauseSetContext,
        "awardIdToCopy",
        null
      )
    ),
    solicitationId: tointeger(
      index(
        ri!clauseSetContext,
        "solicitationIdToCopy",
        null
      )
    )
  ),
  a!map(
    processModel: cons!AS_GCM_PM_COPY_CLAUSE_SET,
    processParameters: {
      newClauseSet: rule!AS_CO_UT_updateCdtFieldsByFieldValuePairs(
        cdt: rule!AS_GCM_updateClauseSetDetailsfromClauseSetContext(clauseSetContext: ri!clauseSetContext),
        /*Capturing copied set ID in new clause set*/
        fieldValuePairs: {
          field: "copiedSetId",
          value: local!clauseSetToCopy.setId
        }
      ),
      /*AS_GCM_ClauseSet */
      clauseSetContext: rule!AS_GCM_DefaultClauseSetContextFields(ri!clauseSetContext),
      /*Map */
      clauseSetToCopy: local!clauseSetToCopy,
      /*AS_GCM_ClauseSet */
      skipUIT: a!defaultValue(
        value: index(ri!clauseSetContext, "skipUIT", null()),
        default: true()
      ),
      /* Boolean */
      copyFillins: a!defaultValue(
        value: index(
          ri!clauseSetContext,
          "copyFillins",
          null()
        ),
        default: true()
      )/* Boolean */

    }
  )
)
