sail_code:
a!localVariables(
  local!bundle: rule!AS_GCM_CO_UT_loadBundleFromFolder(langISOCode: null()),
  #"SYSTEM_SYSRULES_recordFilterList"(
    name: "Dollar Amount",
    options: if(
      rule!AS_CO_UT_isNotBlank(ri!filterField),
      {
        #"SYSTEM_SYSRULES_recordFilterListOption"(
          id: 1,
          name: concat(
            "Under",
            " ",
            cons!AS_GCM_TEXT_DOLLAR_AMOUNT_FILTER_OPTIONS[1]
          ),
          filter: #"SYSTEM_SYSRULES_queryFilter"(
            field: ri!filterField,
            operator: "<",
            value: cons!AS_GCM_INT_DOLLAR_AMOUNT_FILTER_VALUES[1]
          )
        ),
        #"SYSTEM_SYSRULES_recordFilterListOption"(
          id: 2,
          name: cons!AS_GCM_TEXT_DOLLAR_AMOUNT_FILTER_OPTIONS[2],
          filter: #"SYSTEM_SYSRULES_queryFilter"(
            field: ri!filterField,
            operator: "between",
            value: {
              cons!AS_GCM_INT_DOLLAR_AMOUNT_FILTER_VALUES[1],
              cons!AS_GCM_INT_DOLLAR_AMOUNT_FILTER_VALUES[2] + 0.99
            }
          )
        ),
        #"SYSTEM_SYSRULES_recordFilterListOption"(
          id: 3,
          name: cons!AS_GCM_TEXT_DOLLAR_AMOUNT_FILTER_OPTIONS[3],
          filter: #"SYSTEM_SYSRULES_queryFilter"(
            field: ri!filterField,
            operator: "between",
            value: {
              cons!AS_GCM_INT_DOLLAR_AMOUNT_FILTER_VALUES[2] + 1,
              cons!AS_GCM_INT_DOLLAR_AMOUNT_FILTER_VALUES[3]
            }
          )
        ),
        #"SYSTEM_SYSRULES_recordFilterListOption"(
          id: 4,
          name: concat(
            "Over",
            " ",
            cons!AS_GCM_TEXT_DOLLAR_AMOUNT_FILTER_OPTIONS[4]
          ),
          filter: #"SYSTEM_SYSRULES_queryFilter"(
            field: ri!filterField,
            operator: ">",
            value: cons!AS_GCM_INT_DOLLAR_AMOUNT_FILTER_VALUES[3]
          )
        )
      },
      {}
    ),
    allowMultipleSelections: true
  )
)
