sail_code:
rule!AS_GCM_setRecordFields(
  record: ri!ruleConditions,
  neverOverwrite: {
    a!map(
      field: recordType!AS GCM QNM T Rule Condition.createdBy,
      value: ri!initiator
    ),
    a!map(
      field: recordType!AS GCM QNM T Rule Condition.createdDatetime,
      value: now()
    ),
    a!map(
      field: recordType!AS GCM QNM T Rule Condition.isActive,
      value: true()
    )
  },
  alwaysOverwrite: {
    a!map(
      field: recordType!AS GCM QNM T Rule Condition.modifiedBy,
      value: ri!initiator
    ),
    a!map(
      field: recordType!AS GCM QNM T Rule Condition.modifiedDatetime,
      value: now()
    )
  }
)
