sail_code:
if(
  rule!AS_CO_UT_isBlank(ri!text),
  if(ri!showHyphen = true, "-", ""),
  or(
    rule!AS_CO_UT_isBlank(ri!shortenToLen),
    ri!shortenToLen = 0
  ),
  ri!text,
  a!localVariables(
    local!shortenIfLongerThanLen: rule!AS_CO_UT_replaceNull(
      nullableValue: ri!shortenIfLongerThanLen,
      replacementValue: ri!shortenToLen
    ),
    local!showLess: true,
    {
      if(
        len(ri!text) <= local!shortenIfLongerThanLen,
        ri!text,
        {
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: if(
              local!showLess = true,
              trim(
                rule!AS_CO_UT_left(text: ri!text, numChars: ri!shortenToLen)
              ),
              ri!text
            ),
            color: ri!textColor
          ),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            color: ri!linkColor,
            text: concat(
              "...",
              if(
                local!showLess = true,
                a!defaultValue(
                  value: ri!showMoreText,
                  default: lower(
                    "Show More"
                  )
                ),
                a!defaultValue(
                  value: ri!showLessText,
                  default: lower(
                    "Show Less"
                  )
                )
              )
            ),
            link: #"SYSTEM_SYSRULES_dynamicLink"(
              saveInto: a!save(
                local!showLess,
                if(local!showLess = true, false, true)
              )
            ),
            linkStyle: "INLINE"
          )
        }
      )
    }
  )
)
