sail_code:
if(
  rule!AS_CO_UT_isNotBlank(ri!childRecordConfigMaps),
  #"SYSTEM_SYSRULES_richTextBulletedList"(
    items: #"SYSTEM_SYSRULES_forEach"(
      items: ri!childRecordConfigMaps,
      expression: #"SYSTEM_SYSRULES_richTextListItem"(
        text: concat(
          fv!item.displayName,
          char(32),
          "(",
          "Only Referenced by Parent",
          ")"
        ),
        nestedList: rule!DEPRECATED_AS_GCM_CEDM_DSP_dependentRecordsAsNestedList(
          bundle: ri!bundle,
          childRecordConfigMaps: rule!AS_GCM_filterRecords(
            records: ri!configMaps,
            field: "uuid",
            value: difference(
              {
                fv!item.dependentRecordUuids,
                fv!item.precedentRecordFieldMaps.precedentRecordUuid
              },
              ri!uuidsToExport
            )
          ),
          configMaps: ri!configMaps,
          uuidsToExport: append(
            ri!uuidsToExport,
            ri!childRecordConfigMaps.uuid
          )
        )
      )
    )
  ),
  null
)
