    rule!AS_CO_UT_booleanDefaultTrue(boolean: ri!removeAllCiteTags),
    #"urn:appian:function:v1:a:update"(
      local!clauseSetClauseText,
      "clauseText",
      rule!AS_GCM_removeCiteTagsFromHTML(
        text: local!clauseSetClauseText.clauseText
      )
    ),
    local!clauseSetClauseText
  )
)
