sail_code:
a!localVariables(
  local!showLess: true,
  local!isDisplayLink: count(ri!item) > ri!shortenIfLongerThanCount,
  local!itemsToShow: rule!AS_CO_UT_rejectNullValuesFromArray(
    if(
      local!showLess = true,
      index(
        ri!item,
        enumerate(ri!shortenIfLongerThanCount) + 1,
        null
      ),
      ri!item
    )
  ),
  {
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: if(
        local!showLess = true,
        ri!showMoreText,
        ri!showLessText
      ),
      link: #"SYSTEM_SYSRULES_dynamicLink"(
        saveInto: a!save(
          local!showLess,
          if(local!showLess = true, false, true)
        )
      ),
      size: a!defaultValue(
        value: ri!linkTextSize,
        default: "STANDARD"
      ),
      linkStyle: a!defaultValue(
        value: ri!linkStyle,
        default: "STANDALONE"
      ),
      showWhen: and(
        ri!displayLinkAboveContent = true(),
        local!isDisplayLink
      ),
      style: a!defaultValue(
        value: ri!linkTextStyle,
        default: "STRONG"
      )
    ),
    #"SYSTEM_SYSRULES_richTextBulletedList"(
      items: { local!itemsToShow },
      showWhen: ri!isBulletedList = true()
    ),
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: {
        if(
          and(
            ri!displayLinkAboveContent = true(),
            local!isDisplayLink,
            ri!showLinkInNewLine = true()
          ),
          char(10),
          null()
        ),
        joinarray({ local!itemsToShow }, char(10)),
        if(
          and(
            ri!displayLinkAboveContent <> true(),
            local!isDisplayLink,
            ri!showLinkInNewLine = true()
          ),
          char(10),
          {}
        )
      },
      showWhen: ri!isBulletedList <> true()
    ),
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: if(
        local!showLess = true,
        ri!showMoreText,
        ri!showLessText
      ),
      link: #"SYSTEM_SYSRULES_dynamicLink"(
        saveInto: a!save(
          local!showLess,
          if(local!showLess = true, false, true)
        )
      ),
      size: a!defaultValue(
        value: ri!linkTextSize,
        default: "STANDARD"
      ),
      linkStyle: a!defaultValue(
        value: ri!linkStyle,
        default: "STANDALONE"
      ),
      style: a!defaultValue(
        value: ri!linkTextStyle,
        default: "STRONG"
      ),
      showWhen: and(
        ri!displayLinkAboveContent <> true(),
        local!isDisplayLink
      )
    )
  }
)
