a!localVariables(
  local!allModels: a!flatten(
    {
      a!map(
        label: "Anthropic Claude Haiku 3.0 (Fast)",
        value: cons!AIA_MODEL_HAIKU_3,
      ),
      a!map(
        label: "Anthropic Claude Haiku 4.5 (Fast)",
        value: cons!AIA_MODEL_HAIKU_4_5,
      ),
      a!map(
        label: "Anthropic Claude Sonnet 3.0 (Legacy, Reasoning)",
        value: cons!AIA_MODEL_SONNET_3,
      ),
      a!map(
        label: "Anthropic Claude Sonnet 3.5 (Reasoning)",
        value: cons!AIA_MODEL_SONNET_3_5
      ),
      a!map(
        label: "Anthropic Claude Sonnet 3.7 (Reasoning)",
        value: cons!AIA_MODEL_SONNET_3_7
      ),
      a!map(
        label: "Anthropic Claude Sonnet 4.5 (Reasoning)",
        value: cons!AIA_MODEL_SONNET_4_5
      ),
      if(
        ri!includeVision,
        {
          /*model values iwth | to split name and mode are used in AI Reviewer/Judge*/
          a!map(
            label: "Anthropic Claude Haiku 4.5 (Vision)",
            value: cons!AIA_MODEL_HAIKU_4_5_MULTIMODAL,
          ),
          a!map(
            label: "Anthropic Claude Sonnet 3.5 (Vision)",
            value: cons!AIA_MODEL_SONNET_3_5_MULTIMODAL
          ),
          a!map(
            label: "Anthropic Claude Sonnet 3.7 (Vision)",
            value: cons!AIA_MODEL_SONNET_3_7_MULTIMODAL
          ),
          a!map(
            label: "Anthropic Claude Sonnet 4.5 (Vision)",
            value: cons!AIA_MODEL_SONNET_4_5_MULTIMODAL
          )
        },
        null
      )
    }
  ),
  if(
    a!defaultValue(ri!returnOnlySupportedLlms, true),
    a!localVariables(
      local!supportedModels: a!genAiModels(),
      reject(
        fn!isnull,
        #"SYSTEM_SYSRULES_forEach"(
          items: local!allModels,
          expression: if(
            or(
              a!isNullOrEmpty(fv!item.value),
              contains(
                touniformstring(local!supportedModels.id),
                tostring(split(fv!item.value,"|")[1])
              )
            ),
            fv!item,
            null
          )
        )
      )
    ),
    local!allModels
  )
)

