if(
  a!isNullOrEmpty(ri!dateTime),
  "",
  cast(
    'type!{http://www.appian.com/ae/types/2009}Text',
    if(
      ri!isDisplayTime,
      text(ri!dateTime, "mmm d, yyyy h:mm a"),
      text(ri!dateTime, "mmm d, yyyy")
    )
  )
)

if(
  a!defaultValue(ri!showWhen, true),
  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!tabs,
          expression: #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_cardLayout"(
                contents: #"SYSTEM_SYSRULES_richTextDisplayField"(
                  labelPosition: "COLLAPSED",
                  value: {
                    #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: fv!item.label,
                      style: if(
                        fv!index = ri!currentTab,
                        "STRONG",
                        "PLAIN"
                      )
                    ),
                    if(
                      a!isNullOrEmpty(fv!item.count),
                      {},
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: { " ", concat("(", fv!item.count, ")") },
                        style: if(
                          fv!index = ri!currentTab,
                          "STRONG",
                          "PLAIN"
                        )
                      )
                    )
                  },
                  accessibilityText: if(
                    fv!index = ri!currentTab,
                    #"urn:appian:translation-string:v1:c4fe0b43-af2b-44c8-b102-eef93af6d4c3",
                    ""
                  ),
                  align: "CENTER"
                ),
                link: #"SYSTEM_SYSRULES_dynamicLink"(
                  value: fv!index,
                  saveInto: { ri!currentTab },
                  showWhen: and(
                    fv!item <> " ",
                    a!defaultValue(ri!isClickable, true)
                  )
                ),
                accessibilityText: if(
                  fv!index = ri!currentTab,
                  #"urn:appian:translation-string:v1:c4fe0b43-af2b-44c8-b102-eef93af6d4c3",
                  {}
                ),
                marginBelow: "NONE",
                height: "AUTO",
                style: if(
                  fv!index = ri!currentTab,
                  "#F2F4FD",
                  "TRANSPARENT"
                ),
                showBorder: false(),
                padding: "LESS"
              ),
              #"SYSTEM_SYSRULES_horizontalLine"(
                marginBelow: "NONE",
                color: if(
                  ri!currentTab = fv!index,
                  ri!brandingMap.AccentColor,
                  ri!brandingMap.Gray1
                ),
                weight: "MEDIUM"
              )
            },
            showWhen: a!defaultValue(fv!item.showWhen, true),
            width: "MEDIUM"
          )
        ),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            #"SYSTEM_SYSRULES_horizontalLine"(
              marginAbove: "EVEN_LESS",
              marginBelow: "NONE",
              color: ri!brandingMap.Gray1,
              weight: "MEDIUM"
            )
          },
          width: "AUTO"
        )
      },
      marginBelow: "NONE",
      alignVertical: "BOTTOM",
      spacing: "NONE"
    )
  },
  {}
)

