a!localVariables(
  local!default: cons!AIA_MODEL_HAIKU_4_5,
  if(
    /*check default model is enabled in env*/
    contains(
      a!genAiModels().id,
      local!default
    ),
    /*if so, use default*/
    local!default,
    /*else default to first available model*/
    tostring(index(a!genAiModels().id,1,""))
  )
)

