    a!isNullOrEmpty(local!activeOutcomes),
    null,
    local!activeOutcomes[1]
  ),
  #"SYSTEM_SYSRULES_headerContentLayout"(
    header: rule!AIA_DisplayValidationWarningCard(
      validations: local!validations,
      brandingMap: local!brandingMap
    ),
    contents: #"SYSTEM_SYSRULES_paneLayout"(
      panes: {
        #"SYSTEM_SYSRULES_pane"(
          contents: {
            /*Instance Details*/
            #"SYSTEM_SYSRULES_sideBySideLayout"(
              items: {
                #"SYSTEM_SYSRULES_sideBySideItem"(
                  item: #"SYSTEM_SYSRULES_headingField"(
                    text: #"urn:appian:translation-string:v1:56ed94f9-400a-4d9f-8789-6984e1a8a671",
                    headingTag: "H2",
                    marginBelow: "NONE",
                    size: "SMALL",
                    color: local!brandingMap.Gray4,
                    fontWeight: "SEMI_BOLD"
                  )
                ),
                /* Test case */
                #"SYSTEM_SYSRULES_sideBySideItem"(
                  item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                    labelPosition: "COLLAPSED",
                    value: a!localVariables(
                      local!testCase: a!refreshVariable(
                        value: ri!classificationInstance[recordType!AIA Classification Instance.testInstance],
                        refreshOnVarChange: ri!classificationInstance
                      ),
                      if(
                        local!testCase,
                        #"SYSTEM_SYSRULES_richTextItem_v1"(
                          text: {
                            #"SYSTEM_SYSRULES_richTextIcon"(icon: "flask", size: "SMALL"),
                            " ",
                            #"urn:appian:translation-string:v1:21705970-1db4-4bc0-9664-f32b86194327"
                          },
                          size: "SMALL",
                          color: local!brandingMap.Gray4
                        ),
                        {}
                      )
                    ),
                    marginBelow: "NONE"
                  ),
                  showWhen: ri!classificationInstance[recordType!AIA Classification Instance.testInstance],
                  width: "MINIMIZE"
                )
              },
              alignVertical: "MIDDLE"
            ),
            /*Is Sampling Required*/
            rule!AIA_richTextDisplaySamplingFlag(
              Instance: ri!classificationInstance,
              isSpotCheckRequiredField: recordType!AIA Classification Instance.isSamplingRequired,
              brandingMap: local!brandingMap,
              labelLocation: "ADJACENT"
            ),
            /*Status*/
            #"SYSTEM_SYSRULES_cardLayout"(
              contents: {
                #"SYSTEM_SYSRULES_columnsLayout"(
                  columns: {
                    #"SYSTEM_SYSRULES_columnLayout"(
                      contents: #"SYSTEM_SYSRULES_cardLayout"(
                        contents: {
                          #"SYSTEM_SYSRULES_richTextDisplayField"(
                            labelPosition: "COLLAPSED",
                            value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                              text: {
                                #"SYSTEM_SYSRULES_richTextIcon"(
                                  icon: if(
                                    ri!classificationInstance[recordType!AIA Classification Instance.isCorrect],
                                    ri!classificationInstance[recordType!AIA Classification Instance.refInstanceStatus.icon],
                                    "ban"
                                  ),
                                  caption: if(
                                    ri!classificationInstance[recordType!AIA Classification Instance.isCorrect],
                                    ri!classificationInstance[recordType!AIA Classification Instance.refInstanceStatus.label],
                                    #"urn:appian:translation-string:v1:970b8414-e656-4d3a-93bf-00a63ec5cb24"
                                  ),
                                  altText: if(
                                    ri!classificationInstance[recordType!AIA Classification Instance.isCorrect],
                                    ri!classificationInstance[recordType!AIA Classification Instance.refInstanceStatus.label],
                                    #"urn:appian:translation-string:v1:970b8414-e656-4d3a-93bf-00a63ec5cb24"
                                  ),
                                  color: if(
                                    ri!classificationInstance[recordType!AIA Classification Instance.isCorrect],
                                    index(local!brandingMap,ri!classificationInstance[recordType!AIA Classification Instance.refInstanceStatus.iconColor],null),
                                    local!brandingMap.Orange3
                                  )
                                )
                              }
                            ),
                            marginAbove: "STANDARD",
                            marginBelow: "STANDARD",
                            align: "CENTER"
                          )
                        },
                        style: if(
                          ri!classificationInstance[recordType!AIA Classification Instance.isCorrect],
                          a!match(
                            value: ri!classificationInstance[recordType!AIA Classification Instance.statusId],
                            equals: cons!AIA_INSTANCE_STATUS_INITIATED,
                            then: local!brandingMap.Gray1,
                            equals: cons!AIA_INSTANCE_STATUS_CLASSIFIED,
                            then: local!brandingMap.Blue1,
                            equals: cons!AIA_INSTANCE_STATUS_RECONCILED_MANUALLY,
                            then: local!brandingMap.Green1,
                            equals: cons!AIA_INSTANCE_STATUS_CLASSIFIED_WITH_ERRORS,
                            then: local!brandingMap.Red1,
                            equals: cons!AIA_INSTANCE_STATUS_RECONCILED_AUTOMATICALLY,
                            then: local!brandingMap.Blue1,
                            default: local!brandingMap.Gray1
                          ),
                          local!brandingMap.Orange1
                        ),
                        showBorder: false,
                        padding: "LESS",
                        shape: "SEMI_ROUNDED"

                      ),
                      width: "EXTRA_NARROW"
                    ),
                    #"SYSTEM_SYSRULES_columnLayout"(
                      contents: #"SYSTEM_SYSRULES_richTextDisplayField"(
                        label: #"urn:appian:translation-string:v1:f04e5814-41aa-4705-bb0e-968fe8fc8ec5",
                        labelPosition: "COLLAPSED",
                        value: {
                          /*Status Label*/
                          #"SYSTEM_SYSRULES_richTextItem_v1"(
                            text: if(
                              ri!classificationInstance[recordType!AIA Classification Instance.isCorrect],
                              ri!classificationInstance[recordType!AIA Classification Instance.refInstanceStatus.label],
                              #"urn:appian:translation-string:v1:970b8414-e656-4d3a-93bf-00a63ec5cb24"
                            ),
                            size: "STANDARD",
                            color: local!brandingMap.Gray3
                          ),
                          char(10),
                          /*Classification / Accuracy*/
                          if(
                            a!isNullOrEmpty(local!activeOutcomes),
                            "-",
                            if(
                              ri!classificationInstance[recordType!AIA Classification Instance.isCorrect],
                              /*Correct Classification Display (for both single outcome and multi-outcome)*/
                              /*In this case, all local!activeOutcomes are accurate and haven't been added*/
                              #"SYSTEM_SYSRULES_richTextItem_v1"(
                                text: if(
                                  length(local!activeOutcomes) = 1,
                                  /*defaultValue here in case classification is null (in the case of an instance error)*/
                                  a!defaultValue(
                                    local!activeOutcomes[1][recordType!AIA Classification Instance Outcome.classification],
                                    "-"
                                  ),
                                  #"urn:appian:translation-string:v1:7b379b43-baf1-4c0f-b5e7-bf199beee7a0"
                                ),
                                size: "MEDIUM",
                                color: local!brandingMap.Gray4
                              ),
                              /*Incorrect Classification Displays*/
                              if(
                                ri!classificationInstance[recordType!AIA Classification Instance.modelVersion.allowMultipleOutputs] = false,
                                /*Single Outcome Incorrect*/
                                {
                                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                                    text: local!activeOutcomes[1][recordType!AIA Classification Instance Outcome.classification] & " ",
                                    size: "MEDIUM",
                                    color: local!brandingMap.Gray3
                                  ),
                                  #"SYSTEM_SYSRULES_richTextIcon"(
                                    icon: "arrow-right",
                                    altText: #"urn:appian:translation-string:v1:b9f8426e-587d-4944-8fe1-c1ec27a893b6",
                                    size: "MEDIUM",
                                    color: local!brandingMap.Gray3
                                  ),
                                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                                    text: " " & local!activeOutcomes[1][recordType!AIA Classification Instance Outcome.classificationOverride],
                                    size: "MEDIUM",
                                    style: "STRONG"
                                  )
                                },
                                /*Multiple Outcome Incorrect (Accuracy Displayed)*/
                                {
                                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                                    text: local!accuracyPercentageAttributes.percentage & " " & #"urn:appian:translation-string:v1:1d85cd95-b854-411a-907a-67f3ec9b03bd",
                                    size: "MEDIUM",
                                    color: local!brandingMap.Gray4
                                  )
                                }
                              )
                            )
                          )
                        }
                      )
                    )
                  },
                  alignVertical: "MIDDLE"
                )
              },
              accessibilityText: #"urn:appian:translation-string:v1:56ed94f9-400a-4d9f-8789-6984e1a8a671",
              marginBelow: "STANDARD",
              height: "AUTO",
              style: local!brandingMap.White,
              showBorder: if(
                ri!classificationInstance[recordType!AIA Classification Instance.modelVersion.allowMultipleOutputs] = true,
                true,
                local!brandingMap.CardShowBorder
              ),
              borderColor: local!brandingMap.Gray2,
              shape: "SEMI_ROUNDED"
            ),
            /*Reasoning - Only Displayed Here if Single-Outcome Model Version (only 1 outcome will ever exist in this case)*/
            #"SYSTEM_SYSRULES_sectionLayout_v1"(
              showWhen: and(
                ri!classificationInstance[recordType!AIA Classification Instance.modelVersion.allowMultipleOutputs] = false,
                a!isNotNullOrEmpty(local!activeOutcomes),
                a!isNotNullOrEmpty(ri!classificationInstance[recordType!AIA Classification Instance.outcomes.classification][1]),
                a!isNotNullOrEmpty(ri!classificationInstance[recordType!AIA Classification Instance.outcomes.reasoning][1]),
              ),
              contents: {
                #"SYSTEM_SYSRULES_headingField"(
                  text: #"urn:appian:translation-string:v1:aa5d9587-dd60-403b-9130-b87a91526023"(
                    #"urn:appian:translation-variable:v1:bda9b49e-cf18-4b5f-bfc4-941315560b60": ri!classificationInstance[recordType!AIA Classification Instance.outcomes.classification][1]
                  ),
                  headingTag: "H2",
                  size: "SMALL",
                  color: local!brandingMap.Gray4,
                  fontWeight: "SEMI_BOLD"
                ),
                #"SYSTEM_SYSRULES_cardLayout"(
                  contents: {
                    #"SYSTEM_SYSRULES_textField"(
                      labelPosition: "COLLAPSED",
                      value: ri!classificationInstance[recordType!AIA Classification Instance.outcomes.reasoning][1],
                      readOnly: true
                    )
                  },
                  showBorder: local!brandingMap.CardShowBorder,
                  shape: local!brandingMap.CardShape
                )
              }
            ),
            /*Model Version Name/Link*/
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: rule!AIA_ModelVersionName(
                  ModelVersion: ri!classificationInstance[recordType!AIA Classification Instance.modelVersion]
                ),
                link: #"SYSTEM_SYSRULES_recordLink"(
                  recordType: recordType!AIA Classification Model Version,
                  identifier: ri!classificationInstance[recordType!AIA Classification Instance.modelVersionId],
                  targetLocation: #"urn:appian:site-page:v1:15ad6b1a-7c0a-47f8-8803-ac74b16df0df/f6ac5707-0cec-411e-b97a-6e04ee833759"
                ),
                size: "SMALL",
                linkStyle: "STANDALONE"

              ),
              marginBelow: "NONE"
            ),
            /*Last Updated*/
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "COLLAPSED",
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: #"urn:appian:translation-string:v1:10121045-80f7-480a-8072-94ef9f83fcc0"(
                    #"urn:appian:translation-variable:v1:d96e9d1a-32d6-4304-b9ab-771cafe99450": rule!AIA_UTIL_displayDateTime(
                      isDisplayTime: false(),
                      dateTime: ri!classificationInstance[recordType!AIA Classification Instance.modifiedOn]
                    ),
                    #"urn:appian:translation-variable:v1:e1a99a5b-a091-452a-a046-522c57257729": rule!AIA_UTIL_displayUserFullName(
                      userName: ri!classificationInstance[recordType!AIA Classification Instance.modifiedBy]
                    )
                  ),
                  size: "SMALL",
                  color: local!brandingMap.Gray3
                )
              },
              marginAbove: "NONE",
              marginBelow: "STANDARD"
            ),
            /*Multiple Outcome Display*/
            if(
              and(
                ri!classificationInstance[recordType!AIA Classification Instance.modelVersion.allowMultipleOutputs] = true,
                a!isNotNullOrEmpty(local!activeOutcomes)
              ),
              {
                #"SYSTEM_SYSRULES_headingField"(
                  text: #"urn:appian:translation-string:v1:065c0d3b-fef9-44e8-8270-d274c926bffb",
                  headingTag: "H2",
                  marginAbove: "STANDARD",
                  marginBelow: "STANDARD",
                  size: "EXTRA_SMALL",
                  color: local!brandingMap.Gray4,
                  fontWeight: "SEMI_BOLD"
                ),
                #"SYSTEM_SYSRULES_cardLayout"(
                  contents: {
                    #"SYSTEM_SYSRULES_forEach"(
                      items: rule!AIA_UTIL_Filter(
                        records: ri!classificationInstance[recordType!AIA Classification Instance.outcomes],
                        field: recordType!AIA Classification Instance Outcome.outcomeOverride,
                        value: {
                          cons!AIA_TXT_CLASSIFICATION_OUTCOME_OVERRIDE_DELETED,
                          cons!AIA_TXT_CLASSIFICATION_OUTCOME_OVERRIDE_REMOVED
                        },
                        filterType: "REJECT"
                      ),
                      expression: {
                        #"SYSTEM_SYSRULES_cardLayout"(
                          contents: {
                            #"SYSTEM_SYSRULES_sideBySideLayout"(
                              items: {
                                #"SYSTEM_SYSRULES_sideBySideItem"(
                                  item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                                    labelPosition: "COLLAPSED",
                                    value: {
                                      /*Classification / Classification Override*/
                                      if(
                                        and(
                                          a!isNotNullOrEmpty(fv!item[recordType!AIA Classification Instance Outcome.classificationOverride]),
                                          fv!item[recordType!AIA Classification Instance Outcome.outcomeOverride] <> cons!AIA_TXT_CLASSIFICATION_OUTCOME_OVERRIDE_ADDED
                                        ),
                                        /*Classification Overridden Display (for non-Added Outcomes)*/
                                        {
                                          #"SYSTEM_SYSRULES_richTextItem_v1"(
                                            /*defaultValue in case the outcome didn't have any classification to begin with*/
                                            text: a!defaultValue(
                                              fv!item[recordType!AIA Classification Instance Outcome.classification],
                                              "(" & #"urn:appian:translation-string:v1:8581af9c-7279-401c-9ea6-bebe0863fd95" & ")"
                                            ),
                                            color: local!brandingMap.Gray3
                                          ),
                                          " ",
                                          #"SYSTEM_SYSRULES_richTextIcon"(
                                            icon: "arrow-right",
                                            altText: #"urn:appian:translation-string:v1:b9f8426e-587d-4944-8fe1-c1ec27a893b6",
                                            color: local!brandingMap.Gray3
                                          ),
                                          " ",
                                          #"SYSTEM_SYSRULES_richTextItem_v1"(
                                            text: fv!item[recordType!AIA Classification Instance Outcome.classificationOverride],
                                            style: "STRONG"
                                          )
                                        },
                                        /*Non-Overridden Classification (and Added Outcomes) Display*/
                                        #"SYSTEM_SYSRULES_richTextItem_v1"(
                                          /*Added Outcomes use the classificationOverride field, so use that first (otherwise, that field is null)*/
                                          text: a!defaultValue(
                                            fv!item[recordType!AIA Classification Instance Outcome.classificationOverride],
                                            fv!item[recordType!AIA Classification Instance Outcome.classification],
                                            "(" & #"urn:appian:translation-string:v1:8581af9c-7279-401c-9ea6-bebe0863fd95" & ")"
                                          ),
                                          style: "STRONG"
                                        )
                                      ),
                                      char(10),
                                      /*Page Count / Split Pages JSON Override*/
                                      if(
                                        and(
                                          a!isNotNullOrEmpty(fv!item[recordType!AIA Classification Instance Outcome.splitPagesJsonOverride]),
                                          fv!item[recordType!AIA Classification Instance Outcome.outcomeOverride] <> cons!AIA_TXT_CLASSIFICATION_OUTCOME_OVERRIDE_ADDED
                                        ),
                                        /*Split Pages JSON Overridden Display (for non-Added Outcomes)*/
                                        a!localVariables(
                                          local!currentOutcomeSplitPagesJson: rule!AIA_ExtractJson(
                                            json: fv!item[recordType!AIA Classification Instance Outcome.splitPagesJson]
                                          ),
                                          local!currentOutcomeSplitPagesJsonOverride: rule!AIA_ExtractJson(
                                            json: fv!item[recordType!AIA Classification Instance Outcome.splitPagesJsonOverride]
                                          ),
                                          {
                                            #"SYSTEM_SYSRULES_richTextItem_v1"(
                                              text: concat(
                                                #"urn:appian:translation-string:v1:8c69da20-a434-4855-92fd-f04f145cc8a3",
                                                " ",
                                                local!currentOutcomeSplitPagesJson.startPageNumber,
                                                "-",
                                                local!currentOutcomeSplitPagesJson.endPageNumber
                                              ),
                                              size: "SMALL",
                                              color: local!brandingMap.Gray3
                                            ),
                                            " ",
                                            #"SYSTEM_SYSRULES_richTextIcon"(
                                              icon: "arrow-right",
                                              altText: #"urn:appian:translation-string:v1:b9f8426e-587d-4944-8fe1-c1ec27a893b6",
                                              size: "SMALL",
                                              color: local!brandingMap.Gray3
                                            ),
                                            " ",
                                            #"SYSTEM_SYSRULES_richTextItem_v1"(
                                              text: concat(
                                                #"urn:appian:translation-string:v1:8c69da20-a434-4855-92fd-f04f145cc8a3",
                                                " ",
                                                local!currentOutcomeSplitPagesJsonOverride.startPageNumber,
                                                "-",
                                                local!currentOutcomeSplitPagesJsonOverride.endPageNumber
                                              ),
                                              size: "SMALL",
                                              style: "STRONG"
                                            )
                                          }
                                        ),
                                        /*Page Count (Non-Overridden Split Pages JSON)*/
                                        #"SYSTEM_SYSRULES_richTextItem_v1"(
                                          text: if(
                                            fv!item[recordType!AIA Classification Instance Outcome.pageCount] = 1,
                                            fv!item[recordType!AIA Classification Instance Outcome.pageCount] & " " & #"urn:appian:translation-string:v1:e946445c-3ad5-42bc-bfaa-e47a0245446e",
                                            fv!item[recordType!AIA Classification Instance Outcome.pageCount] & " " & #"urn:appian:translation-string:v1:8c69da20-a434-4855-92fd-f04f145cc8a3"
                                          ),
                                          size: "SMALL",
                                          color: local!brandingMap.Gray3
                                        )
                                      )
                                    },
                                    marginBelow: "EVEN_LESS"
                                  )
                                ),
                                #"SYSTEM_SYSRULES_sideBySideItem"(
                                  item: #"SYSTEM_SYSRULES_tagField"(
                                    labelPosition: "COLLAPSED",
                                    tags: {
                                      #"SYSTEM_SYSRULES_tagItem_v1"(
                                        text: #"urn:appian:translation-string:v1:4b3edec0-55e2-4aea-af3c-dc6b50102e62",
                                        showWhen: fv!item[recordType!AIA Classification Instance Outcome.outcomeOverride] = cons!AIA_TXT_CLASSIFICATION_OUTCOME_OVERRIDE_ADDED,
                                        backgroundColor: local!brandingMap.Blue1,
                                        textColor: local!brandingMap.Blue4
                                      ),
                                      #"SYSTEM_SYSRULES_tagItem_v1"(
                                        text: #"urn:appian:translation-string:v1:970b8414-e656-4d3a-93bf-00a63ec5cb24",

                                        showWhen: and(
                                          fv!item[recordType!AIA Classification Instance Outcome.outcomeOverride] <> cons!AIA_TXT_CLASSIFICATION_OUTCOME_OVERRIDE_ADDED,
                                          or(
                                            a!isNotNullOrEmpty(fv!item[recordType!AIA Classification Instance Outcome.classificationOverride]),
                                            a!isNotNullOrEmpty(fv!item[recordType!AIA Classification Instance Outcome.splitPagesJsonOverride])
                                          )
                                        ),
                                        backgroundColor: local!brandingMap.Orange1,
                                        textColor: local!brandingMap.Orange4
                                      )
                                    },
                                    size: "SMALL"
                                  ),
                                  showWhen: or(
                                    fv!item[recordType!AIA Classification Instance Outcome.outcomeOverride] = cons!AIA_TXT_CLASSIFICATION_OUTCOME_OVERRIDE_ADDED,
                                    a!isNotNullOrEmpty(fv!item[recordType!AIA Classification Instance Outcome.classificationOverride]),
                                    a!isNotNullOrEmpty(fv!item[recordType!AIA Classification Instance Outcome.splitPagesJsonOverride])
                                  ),
                                  width: "MINIMIZE"
                                )
                              },
                              alignVertical: "MIDDLE"
                            )
                          },
                          link: #"SYSTEM_SYSRULES_dynamicLink"(
                            saveInto: a!save(local!selectedOutcome, fv!item)
                          ),
                          style: if(
                            local!selectedOutcome = fv!item,
                            local!brandingMap.Blue0,
                            null
                          ),
                          showBorder: false,
                          decorativeBarPosition: if(
                            local!selectedOutcome = fv!item,
                            "START",
                            "NONE"
                          ),
                          decorativeBarColor: local!brandingMap.Blue3
                        ),
                        #"SYSTEM_SYSRULES_horizontalLine"(
                          showWhen: not(fv!isLast),
                          marginBelow: "NONE",
                          color: local!brandingMap.Gray2
                        )
                      }
                    )
                  },
                  showBorder: local!brandingMap.CardShowBorder,
                  padding: "NONE",
                  shape: local!brandingMap.CardShape
                )
              },
              {}
            )
          },
          width: "MEDIUM",
          backgroundColor: if(
            ri!classificationInstance[recordType!AIA Classification Instance.modelVersion.allowMultipleOutputs] = true,
            "WHITE",
            "TRANSPARENT"
          ),
          padding: "LESS"
        ),
        #"SYSTEM_SYSRULES_pane"(
          contents: {
            /*If no outcomes have been written yet (i.e., instance is Initiated), then display nothing*/
            if(
              a!isNullOrEmpty(local!activeOutcomes),
              {},
              if(
                ri!classificationInstance[recordType!AIA Classification Instance.modelVersion.allowMultipleOutputs] = false,
                /*Single Outcome Display*/
                #"SYSTEM_SYSRULES_sectionLayout_v1"(
                  label: #"urn:appian:translation-string:v1:f04e5814-41aa-4705-bb0e-968fe8fc8ec5",
                  marginBelow: "NONE",
                  labelSize: "SMALL",
                  labelColor: local!brandingMap.Gray4
                ),
                /*Multiple Outcome Display*/
                {
                  if(
                    and(
                      local!selectedOutcome[recordType!AIA Classification Instance Outcome.outcomeOverride] <> cons!AIA_TXT_CLASSIFICATION_OUTCOME_OVERRIDE_ADDED,
                      a!isNotNullOrEmpty(local!selectedOutcome[recordType!AIA Classification Instance Outcome.classificationOverride])
                    ),
                    /*Overridden Classification*/
                    #"SYSTEM_SYSRULES_sideBySideLayout"(
                      items: {
                        #"SYSTEM_SYSRULES_sideBySideItem"(
                          item: #"SYSTEM_SYSRULES_headingField"(
                            text: local!selectedOutcome[recordType!AIA Classification Instance Outcome.classification],
                            marginBelow: "NONE",
                            size: "SMALL",
                            color: local!brandingMap.Gray3
                          ),
                          width: "MINIMIZE"
                        ),
                        #"SYSTEM_SYSRULES_sideBySideItem"(
                          item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                            labelPosition: "COLLAPSED",
                            value: {
                              " ",
                              #"SYSTEM_SYSRULES_richTextIcon"(
                                icon: "arrow-right",
                                altText: #"urn:appian:translation-string:v1:b9f8426e-587d-4944-8fe1-c1ec27a893b6",
                                color: local!brandingMap.Gray3
                              ),
                              " "
                            },
                            marginBelow: "NONE"
                          ),
                          width: "MINIMIZE"
                        ),
                        #"SYSTEM_SYSRULES_sideBySideItem"(
                          item: #"SYSTEM_SYSRULES_headingField"(
                            text: local!selectedOutcome[recordType!AIA Classification Instance Outcome.classificationOverride],
                            marginBelow: "NONE",
                            size: "SMALL",
                            fontWeight: "SEMI_BOLD"
                          )
                        )
                      },
                      alignVertical: "MIDDLE",
                      spacing: "DENSE"
                    ),
                    /*Non-Overridden or Added Outcome*/
                    {
                      #"SYSTEM_SYSRULES_sectionLayout_v1"(
                        label: a!defaultValue(
                          local!selectedOutcome[recordType!AIA Classification Instance Outcome.classificationOverride],
                          local!selectedOutcome[recordType!AIA Classification Instance Outcome.classification]
                        ),
                        marginBelow: "NONE",
                        labelSize: "SMALL",
                        labelColor: local!brandingMap.Gray4
                      )
                    }
                  ),
                  /*Reasoning*/
                  rule!AIA_ViewMoreField(
                    numChars: 200,
                    label: #"urn:appian:translation-string:v1:aa5d9587-dd60-403b-9130-b87a91526023"(
                      #"urn:appian:translation-variable:v1:bda9b49e-cf18-4b5f-bfc4-941315560b60": local!selectedOutcome[recordType!AIA Classification Instance Outcome.classification]
                    ),
                    value: local!selectedOutcome[recordType!AIA Classification Instance Outcome.reasoning],
                    labelPosition: "ABOVE",
                    showWhen: and(
                      a!isNotNullOrEmpty(local!selectedOutcome[recordType!AIA Classification Instance Outcome.classification]),
                      a!isNotNullOrEmpty(local!selectedOutcome[recordType!AIA Classification Instance Outcome.reasoning])
                    )
                  )
                }
              ),
            ),
            rule!AIA_UTIL_cardAsTabs(
              tabs: local!tabs,
              currentTab: local!currentTab,
              brandingMap: local!brandingMap
            ),
            #"SYSTEM_SYSRULES_sectionLayout_v1"(
              contents: {
                a!match(
                  value: local!currentTab,
                  equals: 1,
                  /* Document Preview */
                  then: rule!AIA_UTIL_displayInlineDocument(
                    /*For single outcomes, the document ID will always be the converted document from the instance (i.e., ri!classificationInstance.documentId)*/
                    /*So, in all cases, we can just display the selectedOutcome's document here*/
                    documentId: if(
                      a!isNullOrEmpty(local!activeOutcomes),
                      a!defaultValue(
                        ri!classificationInstance[recordType!AIA Classification Instance.documentId],
                        ri!classificationInstance[recordType!AIA Classification Instance.sourceDocumentId]
                      ),
                      local!selectedOutcome[recordType!AIA Classification Instance Outcome.documentId]
                    )
                  ),
