_a-0000ee66-4fc0-8000-9baa-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_forEach"(
  items: ri!modelTriggers,
  expression: #"SYSTEM_SYSRULES_cardLayout"(
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          /*Trigger Type Column*/
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: #"SYSTEM_SYSRULES_stampField"(
              labelPosition: "COLLAPSED",
              icon: fv!item[recordType!AIA Model Trigger.refTriggerType.icon],
              backgroundColor: ri!brandingMap.AppianLight,
              contentColor: ri!brandingMap.AppianPrimary,
              size: "TINY",
              tooltip: fv!item[recordType!AIA Model Trigger.refTriggerType.label],
              shape: "SQUARED"
            ),
            width: "EXTRA_NARROW"
          ),
          /*Name and detals column*/
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              /*Trigger process name*/
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                labelPosition: "COLLAPSED",
                value: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: rule!SHRD_GetProcessModelNameFromId(
                      id: fv!item[recordType!AIA Model Trigger.processModelUuid]
                    ),
                    style: "STRONG"
                  )
                },
                align: "LEFT",
                marginBelow: "NONE"
              ),
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                labelPosition: "COLLAPSED",
                value: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: concat(
                      if(
                        fv!item[recordType!AIA Model Trigger.isSynchronous],
                        #"urn:appian:translation-string:v1:264fd99c-7718-430c-a100-a1462914c8de",
                        #"urn:appian:translation-string:v1:968686e1-ab6e-42c3-aefb-986eb82ec4ad"
                      ),
                      cons!SHRD_DOT_SEPARATOR,
                      #"urn:appian:translation-string:v1:5545bd85-2c1a-4aa5-9f42-672336c38f2d"(
                        #"urn:appian:translation-variable:v1:d739d723-2606-4454-91ec-e47b6fd5ac6d": rule!AIA_UTIL_displayUserFullName(
                          userName: fv!item[recordType!AIA Model Trigger.modifiedBy]
                        ),
                        #"urn:appian:translation-variable:v1:844fd2a2-9f07-448d-89ea-2954915c2c7d": rule!AIA_UTIL_displayDateTime(
                          isDisplayTime: true,
                          dateTime: fv!item[recordType!AIA Model Trigger.modifiedOn]
                        )
                      )
                    ),
                    color: ri!brandingMap.gray3,
                    size: "SMALL",
                    style: "PLAIN"
                  )
                },
                align: "LEFT",
                marginAbove: "NONE"
              )
            },
            width: "10X"
          ),
          /*actions column*/
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: #"SYSTEM_SYSRULES_sideBySideLayout"(
              items: {
                #"SYSTEM_SYSRULES_sideBySideItem"(
                  item: #"SYSTEM_SYSRULES_recordActionField_v1"(
                    actions: {
                      #"SYSTEM_SYSRULES_recordActionItem"(
                        action: #"urn:appian:record-action:v1:20c282f9-a7c8-4146-a217-f5cb2f5cf25f/caede2be-8009-47d6-a8e3-30a966323f41",
                        identifier: fv!item[recordType!AIA Model Trigger.modelTriggerId]
                      )
                    },
                    style: "LINKS",
                    display: "ICON",
                    align: "END"

                  )
                ),
                #"SYSTEM_SYSRULES_sideBySideItem"(
                  item: #"SYSTEM_SYSRULES_recordActionField_v1"(
                    actions: {
                      #"SYSTEM_SYSRULES_recordActionItem"(
                        action: #"urn:appian:record-action:v1:20c282f9-a7c8-4146-a217-f5cb2f5cf25f/9762cf8e-d5d9-4186-8bb0-e99bfc617fa6",
                        identifier: fv!item[recordType!AIA Model Trigger.modelTriggerId]
                      )
                    },
                    style: "LINKS",
                    display: "ICON",
                    align: "END"

                  ),
                  width: "1X"
                )
              },
              alignVertical: "MIDDLE",
              spacing: "NONE"
            ),
            width: "1X"
          )
        },
        alignVertical: "MIDDLE",
        spacing: "NONE"
      )
    },
    padding: "STANDARD",
    marginBelow: if(
      fv!isLast,
      "NONE",
      "LESS"
    ),
    showBorder: false
  )
)

_a-0000ee89-e9c9-8000-9bb2-011c48011c48_.sail_code:
a!localVariables(
  local!isVision: ri!ClassificationModelVersion[recordType!AIA Classification Model Version.useVision],
  {
    #"SYSTEM_SYSRULES_sideBySideLayout"(
      alignVertical: "BOTTOM",
      items: {
        #"SYSTEM_SYSRULES_sideBySideItem"(
          width: "MINIMIZE",
          item: #"SYSTEM_SYSRULES_tagField"(
            label: #"urn:appian:translation-string:v1:9c710528-d866-456a-a87c-b3c8591c9574",
            labelPosition: "COLLAPSED",
            tags: if(
              local!isVision,
              #"SYSTEM_SYSRULES_tagItem_v1"(
                text: #"urn:appian:translation-string:v1:ec893136-96e8-420b-b508-f285f71b2e5b",
                backgroundColor: ri!brandingMap.Blue1,
                textColor: ri!brandingMap.Blue4
              ),
              #"SYSTEM_SYSRULES_tagItem_v1"(
                text: #"urn:appian:translation-string:v1:e6b03695-dd8b-4a57-8bbc-fab870dfbd1e",
                backgroundColor: ri!brandingMap.Blue1,
                textColor: ri!brandingMap.Blue4
              )
            ),
            marginBelow: "LESS"
          )
        ),
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"SYSTEM_SYSRULES_tagField"(
            labelPosition: "COLLAPSED",
            tags: {
              #"SYSTEM_SYSRULES_tagItem_v1"(
                showWhen: and(
                  a!isNotNullOrEmpty(
                    ri!ClassificationModelVersion[recordType!AIA Classification Model Version.allowMultipleOutputs]
                  ),
                  ri!ClassificationModelVersion[recordType!AIA Classification Model Version.allowMultipleOutputs] = true
                ),
                text: #"urn:appian:translation-string:v1:6bd0443a-1d14-4267-99bb-364de260265b",
                backgroundColor: ri!brandingMap.Purple1,
                textColor: ri!brandingMap.Purple4
              )
            },
            marginBelow: "LESS"
          )
        )
      }
    )
  }
)

_a-0000ee5d-151c-8000-9ba8-011c48011c48_.sail_code:
a!localVariables(
  local!brandingMap: a!defaultValue(ri!brandingMap, rule!AIA_UTIL_LoadBrandingMap()),
  #"SYSTEM_SYSRULES_columnsLayout"(
    columns: {
      #"SYSTEM_SYSRULES_columnLayout"(
        contents: {},
        width: "1X"
      ),
      #"SYSTEM_SYSRULES_columnLayout"(
        contents: {
          #"SYSTEM_SYSRULES_imageField_v1"(
            label: #"urn:appian:translation-string:v1:f61e3282-b9ca-4efa-ba46-55c466fba0d0",
            labelPosition: "COLLAPSED",
            images: {
              #"SYSTEM_SYSRULES_documentImage"(
                document: ri!image
              )
            },
            marginAbove: "EVEN_MORE",
            marginBelow: "STANDARD",
            align: "CENTER",
            size: a!defaultValue(ri!imageSize, "SMALL_PLUS"),
            style: "STANDARD",
            isThumbnail: false
          ),
          #"SYSTEM_SYSRULES_headingField"(
            text: ri!header,
            showWhen: a!isNotNullOrEmpty(ri!header),
            marginAbove: "STANDARD",
            marginBelow: "EVEN_LESS",
            align: "CENTER",
            size: "SMALL",
            color: local!brandingMap.Black,
            fontWeight: "SEMI_BOLD"
          ),
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            labelPosition: "COLLAPSED",
            value: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: ri!subheader,
                color: local!brandingMap.Gray3
              )
            },
            marginAbove: "EVEN_LESS",
            marginBelow: "STANDARD",
            align: "CENTER"
          ),
          #"SYSTEM_SYSRULES_columnsLayout"(
            columns: #"SYSTEM_SYSRULES_forEach"(
              ri!buttons,
              #"SYSTEM_SYSRULES_columnLayout"(
                contents: {
                  a!match(
                    value: fv!item.type,
                    equals: cons!AIA_BUTTON_TYPE_LINK,
                    then: #"SYSTEM_SYSRULES_cardLayout"(
                      contents: {
                        #"SYSTEM_SYSRULES_richTextDisplayField"(
                          marginAbove: "EVEN_LESS",
                          marginBelow: "STANDARD",
                          align: "CENTER",
                          value: {
                            #"SYSTEM_SYSRULES_richTextItem_v1"(
                              text: fv!item.label,
                              color: local!brandingMap.AccentColor,
                              style: "STRONG"
                            )
                          }
                        )
                      },
                      borderColor: local!brandingMap.Blue3,
                      shape: local!brandingMap.CardShape,
                      link: fv!item.link,
                      padding: "NONE"
                    ),
                    equals: cons!AIA_BUTTON_TYPE_ACTION,
                    then: fv!item.action,
                    default: {}
                  )
                }
              )
            ),
            showWhen: a!isNotNullOrEmpty(ri!buttons),
            marginAbove: "EVEN_LESS"
          )
        },
        width: "3X"
      ),
      #"SYSTEM_SYSRULES_columnLayout"(
        contents: {},
        width: "1X"
      )
    },
    marginBelow: "EVEN_MORE"
  )
)

