a!localVariables(
  local!tokenTrendUsage: #"SYSTEM_SYSRULES_queryRecordType_v1"(
    recordType: ri!recordType,
    fields: #"SYSTEM_SYSRULES_aggregationFields"(
      measures: {
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!selectedInterval.weeklyGroups,
          expression: #"SYSTEM_SYSRULES_measure"(
            field: ri!outputTokenField,
            function: "SUM",
            alias: concat("outputToken", fv!index),
            filters: append(
              ri!filters,
              #"SYSTEM_SYSRULES_queryFilter"(
                field: ri!createdOnField,
                operator: "between",
                value: {
                  todatetime(fv!item.startDate),
                  todatetime(fv!item.endDate)
                },
                applyWhen: a!isNotNullOrEmpty(ri!selectedInterval)
              )
            )
          )
        ),
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!selectedInterval.weeklyGroups,
          expression: #"SYSTEM_SYSRULES_measure"(
            field: ri!inputTokenField,
            function: "SUM",
            alias: concat("inputToken", fv!index),
            filters: append(
              ri!filters,
              #"SYSTEM_SYSRULES_queryFilter"(
                field: ri!createdOnField,
                operator: "between",
                value: {
                  todatetime(fv!item.startDate),
                  todatetime(fv!item.endDate)
                },
                applyWhen: a!isNotNullOrEmpty(ri!selectedInterval)
              )
            )
          )
        )
      }
    ),
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1)
  ).data,
  local!weekLabels: if(
    ri!selectedInterval.label = #"urn:appian:translation-string:v1:f5eca8bb-66c4-4925-9a10-c2a74b4a1227",
    ri!selectedInterval.weeklyGroups,
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!selectedInterval.weeklyGroups,
      expression: {
        label: concat(
          text(fv!item.startDate, "m/dd"),
          " to ",
          text(fv!item.endDate, "m/dd")
        )
      }
    )
  ),
  {
    #"SYSTEM_SYSRULES_lineChartField_v1"(
      label: #"urn:appian:translation-string:v1:6bdd7618-e3bb-4b70-ae6d-ff86f2fae58c",
      labelPosition: "COLLAPSED",
      categories: index(local!weekLabels, "label", null()),
      series: {
        #"SYSTEM_SYSRULES_chartSeries"(
          label: #"urn:appian:translation-string:v1:1d6f44cb-8de8-48c2-9a6d-ed8379b88438",
          data: #"SYSTEM_SYSRULES_forEach"(
            items: enumerate(
              length(ri!selectedInterval.weeklyGroups)
            ) + 1,
            expression: a!localVariables(
              local!inputToken: if(
                rule!AIA_UTIL_isBlank(
                  tointeger(
                    index(
                      local!tokenTrendUsage,
                      concat("inputToken", fv!item),
                      null()
                    )
                  )
                ),
                0,
                tointeger(
                  index(
                    local!tokenTrendUsage,
                    concat("inputToken", fv!item),
                    null()
                  )
                )
              ),
              local!outputToken: if(
                rule!AIA_UTIL_isBlank(
                  tointeger(
                    index(
                      local!tokenTrendUsage,
                      concat("outputToken", fv!item),
                      null()
                    )
                  )
                ),
                0,
                tointeger(
                  index(
                    local!tokenTrendUsage,
                    concat("outputToken", fv!item),
                    null()
                  )
                )
              ),
              sum(local!inputToken, local!outputToken)
            )
          ),
          color: "ACCENT"
        )
      },
      showLegend: false(),
      showDataLabels: false(),
      showTooltips: true(),
      xAxisStyle: "STANDARD",
      yAxisStyle: "NONE",
      height: "MICRO"
    )
  }
)

