a!localVariables(
  local!extractionModelData: index(
    #"SYSTEM_SYSRULES_queryRecordType_v1"(
      recordType: recordType!AIA Extraction Model,
      fields: #"SYSTEM_SYSRULES_aggregationFields"(
        measures: {
          #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Extraction Model.modelId,
            function: "COUNT",
            alias: "activeModelCount",
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AIA Extraction Model.createdOn,
              operator: "between",
              value: {
                ri!selectedInterval.currrentStartDate,
                ri!selectedInterval.currentEndDate
              }
            )
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Extraction Model.publishedVersionAccuracy,
            function: "AVG",
            alias: "accuracyAvg",
            filters: #"SYSTEM_SYSRULES_queryFilter"(
              field: recordType!AIA Extraction Model.createdOn,
              operator: "between",
              value: {
                ri!selectedInterval.currrentStartDate,
                ri!selectedInterval.currentEndDate
              }
            )
          )
        }
      ),
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1)
    ).data,
    1,
    null()
  ),
  local!modelVersionIds: #"SYSTEM_SYSRULES_queryRecordType_v1"(
    recordType: recordType!AIA Extraction Model,
    fields: {
      recordType!AIA Extraction Model.publishedModelVersionId
    },
    filters: #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!AIA Extraction Model.createdOn,
      operator: "between",
      value: {
        ri!selectedInterval.currrentStartDate,
        ri!selectedInterval.currentEndDate
      }
    ),
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 5000)
  ).data[recordType!AIA Extraction Model.publishedModelVersionId],
  local!extractionInstanceData: index(
    #"SYSTEM_SYSRULES_queryRecordType_v1"(
      recordType: recordType!AIA Extraction Instance,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1),
      fields: #"SYSTEM_SYSRULES_aggregationFields"(
        measures: {
          #"SYSTEM_SYSRULES_measure"(
            field: recordType!AIA Extraction Instance.id,
