    value: #"SYSTEM_SYSRULES_queryRecordType_v1"(
      recordType: ri!recordType,
      fields: #"SYSTEM_SYSRULES_aggregationFields"(
        measures: {
          #"SYSTEM_SYSRULES_measure"(
            field: ri!outputTokenField,
            function: "SUM",
            alias: "outputToken",
            filters: local!filters
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: ri!inputTokenField,
            function: "SUM",
            alias: "inputToken",
            filters: local!filters
          ),
          #"SYSTEM_SYSRULES_measure"(
            field: ri!pageCountField,
            function: "SUM",
            alias: "pageCount",
            filters: local!filters
          ),
