a!localVariables(
  local!previousVersionId: if(
    a!isNullOrEmpty(ri!ClassificationModel),
    null,
    index(
      rule!AIA_QR_getClassificationModelVersion(
        returnType: "SINGLE_OBJECT",
        fields: {
          recordType!AIA Classification Model Version.modelVersionId
        },
        modelId: ri!ClassificationModel[recordType!AIA Classification Model.modelId],
        sort: #"SYSTEM_SYSRULES_sortInfo"(
          field: recordType!AIA Classification Model Version.versionId,
          ascending: false
        )
      ),
      recordType!AIA Classification Model Version.modelVersionId,
      null
    )
  ),
  local!previousVersion: a!defaultValue(
    ri!ClassificationModelVersion,
    rule!AIA_QR_getClassificationModelVersion(
      returnType: "RECORD_BY_ID",
      executeWhen: a!isNotNullOrEmpty(local!previousVersionId),
      modelVersionId: local!previousVersionId,
      fields: {
        recordType!AIA Classification Model Version.categories,
        recordType!AIA Classification Model Version.examples,
        recordType!AIA Classification Model Version.model
      },
      relatedRecordData: #"SYSTEM_SYSRULES_relatedRecordData"(
        relationship: recordType!AIA Classification Model Version.categories,
        sort: #"SYSTEM_SYSRULES_sortInfo"(
          field: recordType!AIA Classification Model Version Category.sortOrder,
          ascending: true
        )
      )
    )
  ),
  #"urn:appian:function:v1:a:update"(
    #"urn:appian:function:v1:a:update"(
      #"urn:appian:function:v1:a:update"(
        local!previousVersion,
        recordType!AIA Classification Model Version.examples,
        /* Examples */
        if(
          a!isNullOrEmpty(local!previousVersion[recordType!AIA Classification Model Version.examples]),
          {},
          #"SYSTEM_SYSRULES_forEach"(
            local!previousVersion[recordType!AIA Classification Model Version.examples],
            #"urn:appian:function:v1:a:update"(
              fv!item,
              recordType!AIA Model Version Example.id,
              null
            )
          )
        )
      ),
      recordType!AIA Classification Model Version.categories,
      /* Categories */
      #"SYSTEM_SYSRULES_forEach"(
        local!previousVersion[recordType!AIA Classification Model Version.categories],
        #"urn:appian:function:v1:a:update"(
          fv!item,
          {recordType!AIA Classification Model Version Category.id,recordType!AIA Classification Model Version Category.modelVersionId},
          {null, null}
        )
      )
    ),
    {
      recordType!AIA Classification Model Version.modelVersionId,
      recordType!AIA Classification Model Version.status,
      recordType!AIA Classification Model Version.versionId,
      recordType!AIA Classification Model Version.updates
    },
    {
      /* Model Version Id */
      null,
      /* Status */
      cons!AIA_MODEL_VERSION_STATUS_DRAFT,
      /* Version Id */
      a!defaultValue(
        ri!ClassificationModel[recordType!AIA Classification Model.maxVersion], 
        ri!ClassificationModelVersion[recordType!AIA Classification Model Version.model.maxVersion],
        0
      ) + 1,
      /* Updates */
      if(
        a!isNotNullOrEmpty(ri!ClassificationModelVersion),
        #"urn:appian:translation-string:v1:e20e8a30-36b0-48cf-b107-7710fdc350c7"(#"urn:appian:translation-variable:v1:c636a6b2-4ebc-451c-927e-95ef2198788e":  ri!ClassificationModelVersion[recordType!AIA Classification Model Version.versionId]),
        null
      )
    }
  )
)

