a!localVariables(
  local!maxBatchSize: 5000,
  /*Chosen return type*/
  local!returnType: a!defaultValue(ri!returnType, "OBJECT_ARRAY"),
  /*Determine the query parameters to use by the return type*/
  local!queryParams: a!match(
    value: local!returnType,
    equals: "OBJECT_ARRAY",
    then: a!map(
      castToType: if(
        a!isNullOrEmpty(ri!groupByFields),
        #"urn:appian:function:v1:a:listtype?okey=a!listType"(ri!recordType),
        'type!{http://www.appian.com/ae/types/2009}Map?list'
      ),
      fetchTotalCount: false,
      isArray: true,
      queryRecordByIdentifier: false,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: a!defaultValue(
          ri!pagingInfo.batchSize,
          local!maxBatchSize
        ),
        sort: { ri!pagingInfo.sort, ri!sort }
      )
    ),
    equals: "SINGLE_OBJECT",
    then: a!map(
      castToType: if(
        a!isNullOrEmpty(ri!groupByFields),
        ri!recordType,
        'type!{http://www.appian.com/ae/types/2009}Map'
      ),
      fetchTotalCount: false,
      isArray: false,
      queryRecordByIdentifier: false,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: 1,
        sort: { ri!pagingInfo.sort, ri!sort }
      )
    ),
    equals: "RECORD_BY_ID",
    then: a!map(
      castToType: ri!recordType,
      fetchTotalCount: false,
      isArray: false,
      queryRecordByIdentifier: true,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: 1,
        sort: { ri!pagingInfo.sort, ri!sort }
      )
    ),
    equals: "DATA_SUBSET",
    then: a!map(
      castToType: 'type!{http://www.appian.com/ae/types/2009}DataSubset',
      fetchTotalCount: true,
      isArray: false,
      queryRecordByIdentifier: false,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: a!defaultValue(ri!pagingInfo.startIndex, 1),
        batchSize: a!defaultValue(
          ri!pagingInfo.batchSize,
          local!maxBatchSize
        ),
        sort: { ri!pagingInfo.sort, ri!sort }
      )
    ),
    equals: "TOTAL_COUNT",
    then: a!map(
      castToType: 'type!{http://www.appian.com/ae/types/2009}Integer',
      fetchTotalCount: true,
      isArray: false,
      queryRecordByIdentifier: false,
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: 0)
    ),
    default: fn!error(
      "Invalid returnType, must be one of: OBJECT_ARRAY, SINGLE_OBJECT, RECORD_BY_ID, DATA_SUBSET, TOTAL_COUNT"
    )
  ),
  if(
    /*Early exit for skip execution*/
    ri!executeWhen = false,
    cast(
      local!queryParams.castToType,
      if(local!queryParams.isArray, {}, null)
    ),
    a!localVariables(
      /*Run the query*/
      local!queryResult: a!refreshVariable(
        value: if(
          local!queryParams.queryRecordByIdentifier,
          #"SYSTEM_SYSRULES_queryRecordByIdentifier"(
            recordType: ri!recordType,
            identifier: index({ri!identifier},1,null),
            /*If fields is sent in using a!selectionFields(), then adding the fields contained in ri!relatedRecordData will break the query*/
            fields: reject(
              a!isNullOrEmpty,
              a!flatten(
                {
                  ri!fields,
                  if(
                    or(
                      typeof(ri!fields) = typeof(#"SYSTEM_SYSRULES_selectionFields"()),
                      typeof(ri!fields) = typeof({#"SYSTEM_SYSRULES_selectionFields"()})
                    ),
                    {},
                    index(ri!relatedRecordData, "relationship", {})
                  )
                }
              )
            ),
            relatedRecordData: ri!relatedRecordData
          ),
          #"SYSTEM_SYSRULES_queryRecordType_v1"(
            recordType: ri!recordType,
            fields: if(
              a!isNullOrEmpty(ri!groupByFields),
              reject(
                a!isNullOrEmpty,
                a!flatten(
                  {
                    ri!fields,
                    index(ri!relatedRecordData, "relationship", {})
                  }
                )
              ),
              #"SYSTEM_SYSRULES_aggregationFields"(
                groupings: #"SYSTEM_SYSRULES_forEach"(
                  items: ri!groupByFields,
                  expression: #"SYSTEM_SYSRULES_grouping"(field: fv!item, alias: tostring(fv!item))
                ),
                measures: #"SYSTEM_SYSRULES_measure"(
                  field: ri!groupByFields[1],
                  function: "COUNT",
                  alias: "count"
                )
              )
            ),
            filters: #"SYSTEM_SYSRULES_queryLogicalExpression"(
              operator: "AND",
              ignoreFiltersWithEmptyValues: true,
              logicalExpressions: {
                ri!logicalExpressions,
                if(
                  and(
                    a!isNotNullOrEmpty(ri!searchFields),
                    a!isNotNullOrEmpty(ri!searchTerm)
                  ),
                  #"SYSTEM_SYSRULES_queryLogicalExpression"(
                    operator: "AND",
                    logicalExpressions: #"SYSTEM_SYSRULES_forEach"(
                      items: split(trim(ri!searchTerm), " "),
                      expression: a!localVariables(
                        local!splitSearchTerm: fv!item,
                        /*   Initiate filter for each field by the value provided in searchTerm   */
                        #"SYSTEM_SYSRULES_queryLogicalExpression"(
                          operator: "OR",
                          filters: #"SYSTEM_SYSRULES_forEach"(
                            items: ri!searchFields,
                            expression: #"SYSTEM_SYSRULES_queryFilter"(
                              field: fv!item,
                              operator: "includes",
                              value: local!splitSearchTerm
                            )
                          )
                        )
                      )
                    )
                  ),
                  {}
                )
              },
              filters: ri!filters
            ),
            pagingInfo: if(
              a!isNullOrEmpty(ri!groupByFields),
              local!queryParams.pagingInfo,
              #"urn:appian:function:v1:a:update"(
                local!queryParams.pagingInfo,
                "sort",
                null
              )
            ),
            fetchTotalCount: local!queryParams.fetchTotalCount,
            relatedRecordData: if(
              a!isNullOrEmpty(ri!groupByFields),
              ri!relatedRecordData,
              {}
            )
          )
        ),
        refreshOnVarChange: ri!triggerRefresh,
        refreshAfter: { "RECORD_ACTION" }
      ),
      /*Format the output*/
      local!output: a!match(
        value: local!returnType,
        equals: "OBJECT_ARRAY",
        then: cast(
          local!queryParams.castToType,
          index(local!queryResult,"data",{})
        ),
        equals: "SINGLE_OBJECT",
        then: cast(
          local!queryParams.castToType,
          index(local!queryResult,"data",{})
        ),
        equals: "RECORD_BY_ID",
        /*do not need to cast for queryRecordByIdentifier*/
        then: local!queryResult,
        equals: "DATA_SUBSET",
        then: cast(
          local!queryParams.castToType,
          local!queryResult
        ),
        equals: "TOTAL_COUNT",
        then: cast(
          local!queryParams.castToType,
          index(local!queryResult,"totalCount",{})
        ),
        default: null
      ),
      local!output
    )
  )
)

_a-0000ee35-0ca2-8000-9ba6-011c48011c48_.sail_code:
a!match(
  value: ri!name,
  /* Classification Model Created */
  equals: cons!AIA_MTR_CLASSIFICATION_MODEL_CREATED,
  then: rule!AIA_MTR_ClassificationModelCreated(),
  /* Classification Instance Created */
  equals: cons!AIA_MTR_CLASSIFICATION_INSTANCE_CREATED,
  then: rule!AIA_MTR_ClassificationInstanceCreated(),
  /* Classification Instance Reconciled */
  equals: cons!AIA_MTR_CLASSIFICATION_INSTANCE_RECONCILED,
  then: rule!AIA_MTR_ClassificationInstanceReconciled(),
  /* Classification Instance with Validation Errors */
  equals: cons!AIA_MTR_INSTANCE_STATUS_CLASSIFIED_WITH_ERRORS,
  then: rule!AIA_MTR_ClassificationInstanceClassifiedWithErrors(),
  /* Classification Instance with Critical Error */
  equals: cons!AIA_MTR_INSTANCE_STATUS_CLASSIFICATION_CRITICAL_ERROR,
  then: rule!AIA_MTR_ClassificationInstanceError(),

  /* Extraction Model Created */
  equals: cons!AIA_MTR_EXTRACTION_MODEL_CREATED,
  then: rule!AIA_MTR_ExtractionModelCreated(),
  /* Extraction Instance Created */
  equals: cons!AIA_MTR_EXTRACTION_INSTANCE_CREATED,
  then: rule!AIA_MTR_ExtractionInstanceCreated(),
  /* Extraction Instance Reconciled */
  equals: cons!AIA_MTR_EXTRACTION_INSTANCE_RECONCILED,
  then: rule!AIA_MTR_ExtractionInstanceReconciled(),
  /* Extraction Instance with Validation Errors */
  equals: cons!AIA_MTR_INSTANCE_STATUS_EXTRACTED_WITH_ERRORS,
  then: rule!AIA_MTR_ExtractionInstanceExtractedWithErrors(),
  /* Extraction Instance with Critical Error */
  equals: cons!AIA_MTR_INSTANCE_STATUS_EXTRACTION_CRITICAL_ERROR,
  then: rule!AIA_MTR_ExtractionInstanceError(),

  /* Triggered when AI review raises an error (field level) */
  equals: cons!AIA_MTR_AI_REVIEW_FIELD,
  then: rule!AIA_MTR_ExtractionInstanceFieldAiReview(),
  /* Triggered when AI review raises an error (instance level, this instance has 1 or more errors raised by AI review) */
  equals: cons!AIA_MTR_AI_REVIEW_INSTANCE,
  then: rule!AIA_MTR_ExtractionInstanceAiReview(),
  /* Triggered when model is created with AI review turned on OR AI review is turned on for a model */
  equals: cons!AIA_MTR_AI_REVIEW_MODEL_VERSION,
  then: rule!AIA_MTR_ExtractionModelVersionAiReview(),
  /* Triggered when user clicks accept */
  equals: cons!AIA_MTR_AI_REVIEW_ACCEPT,
  then: rule!AIA_MTR_ExtractionInstanceValidationAccept(),
  /* Triggered when user clicks ignored */
  equals: cons!AIA_MTR_AI_REVIEW_IGNORE,
  then: rule!AIA_MTR_ExtractionInstanceValidationIgnore(),
  /*triggered when spot check ocnfigured*/
  equals: cons!AIA_MTR_SPOT_CHECK_MODEL_VERSION,
  then: rule!AIA_MTR_ModelVersionConfigureSampling(),
  /* Triggered when a model validation rule of type AI validation creates a validation instance */
  equals: cons!AIA_MTR_AI_VALIDATION_RULE_OCCURANCE,
  then: rule!AIA_MTR_InstanceValidationAiRuleOccurance(),
  /*Confidence Threshold Enabled*/
  equals: cons!AIA_MTR_CONFIDENCE_THRESHOLD_ENABLED,
  then: rule!AIA_MTR_ConfidenceThresholdEnabled(),
  /*Confidence Threshold Disabled*/
  equals: cons!AIA_MTR_CONFIDENCE_THRESHOLD_DISABLED,
  then: rule!AIA_MTR_ConfidenceThresholdDisabled(),
  /*Extraction Model Version Restored*/
  equals: cons!AIA_MTR_EXTRACTION_MODEL_VERSION_RESTORE,
  then: rule!AIA_MTR_ExtractionModelVersionRestore(),
  /*Classification Model Version Restored*/
  equals: cons!AIA_MTR_CLASSIFICATION_MODEL_VERSION_RESTORE,
  then: rule!AIA_MTR_ClassificationModelVersionRestore(),
  /*Model Test Suite Created*/
  equals: cons!AIA_MTR_MODEL_TEST_SUITE_CREATED,
  then: rule!AIA_MTR_ModelTestSuiteCreated(),
  /*Model Test Suite Updated*/
  equals: cons!AIA_MTR_MODEL_TEST_SUITE_UPDATED,
  then: rule!AIA_MTR_ModelTestSuiteUpdated(),
  /*Model Test Run Initiated*/
  equals: cons!AIA_MTR_MODEL_TEST_RUN_INITIATED,
  then: rule!AIA_MTR_ModelTestRunInitiated(),
  /*Classification Model Version Created with Multiple Outputs*/
  equals: cons!AIA_MTR_MULTIPLE_OUTPUTS_CLASSIFICATION_MODEL_VERSION_CREATED,
  then: rule!AIA_MTR_MultipleOutputsClassificationModelVersionCreated(),
  /*Classification Instance Created for a Model Version that Allows Multiple Outputs*/
  equals: cons!AIA_MTR_MULTIPLE_OUTPUTS_CLASSIFICATION_INSTANCE_CREATED,
  then: rule!AIA_MTR_MultipleOutputsClassificationInstanceCreated(),
  /*Classification Instance Reconciled for a Model Version that Allows Multiple Outputs*/
  equals: cons!AIA_MTR_MULTIPLE_OUTPUTS_CLASSIFICATION_INSTANCE_RECONCILED,
  then: rule!AIA_MTR_MultipleOutputsClassificationInstanceReconciled(),
  default: null
)
