          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"SYSTEM_SYSRULES_textField"(
              label: "From",
              labelPosition: "ABOVE",
              value: local!mailMap.from,
              refreshAfter: "UNFOCUS",
              readOnly: true,
              validations: {}
            )
          ),
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"SYSTEM_SYSRULES_textField"(
              label: "To",
              labelPosition: "ABOVE",
              value: local!mailMap.to,
              saveInto: {},
              refreshAfter: "UNFOCUS",
              readOnly: true,
              validations: {}
            )
          )
        }
      ),
      #"SYSTEM_SYSRULES_horizontalLine"(),
