  local!displayRule: a!refreshVariable(
    value: displayvalue(
      ri!validationInstance[recordType!AIA Instance Validation Error.typeId],
      rule!AIA_RULE_ENUM_RuleOperations().typeId,
      rule!AIA_RULE_ENUM_RuleOperations().displayRule,
      null
    ),
    refreshAlways: true
  ),
  if(
    a!isNotNullOrEmpty(local!displayRule),
    local!displayRule(Validation: ri!validationInstance, brandingMap: ri!brandingMap),
    ri!validationInstance[recordType!AIA Instance Validation Error.message]
  )
)

_a-0000ed69-99d8-8000-9ba6-011c48011c48_.sail_code:
a!localVariables(
  local!desiredAccuracy: ri!currentAccuracy + cons!AIA_INT_AREA_IMPROVEMENT_HIGH_RANGE_THRESHOLD_VALUE,
  local!data: index(
    #"SYSTEM_SYSRULES_queryRecordType_v1"(
      recordType: recordType!AIA Classification Model,
      fields: #"SYSTEM_SYSRULES_aggregationFields"(
        measures: {
          /*Total Active Model Count*/
          #"SYSTEM_SYSRULES_measure"(
            alias: "activeModelCount",
            field: recordType!AIA Classification Model.modelId,
            function: "COUNT",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: recordType!AIA Classification Model.publishedAccuracy,
                operator: ">=",
                value: local!desiredAccuracy
              )
            }
          ),
          #"SYSTEM_SYSRULES_measure"(
            alias: "allModelCount",
            field: recordType!AIA Classification Model.modelId,
            function: "COUNT",
            filters: {}
          )
        }
      ),
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 1)
    ).data,
    1,
    null
  ),
  round(
    (
      local!data.activeModelCount / local!data.allModelCount
    ) * 100,
    2
  )
)

_a-0000ed1d-549f-8000-9c66-011c48011c48_.sail_code:
a!localVariables(
  local!isExtraction: runtimetypeof(ri!modelVersion) = typeof(
    recordType!AIA Extraction Model Version()
  ),
  local!fields: if(
    local!isExtraction,
    a!map(
      relationship: recordType!AIA Extraction Model Version.examples
    ),
    a!map(
      relationship: recordType!AIA Classification Model Version.examples
    )
  ),
  local!hasExamples: a!isNotNullOrEmpty(
    reject(
      fn!isnull,
      {
        ri!modelVersion[local!fields.relationship][recordType!AIA Model Version Example.id]
      }
    )
  ),
  if(
    local!hasExamples,
    reject(
      fn!isnull,
      #"SYSTEM_SYSRULES_forEach"(
        cast(
          #"urn:appian:function:v1:a:listtype?okey=a!listType"(
            recordType!AIA Model Version Example
          ),
          #"SYSTEM_SYSRULES_queryEntity_v2"(
            entity: cons!AIA_DSE_MODEL_VERSION_EXAMPLE,
            query: #"SYSTEM_SYSRULES_query"(
              filter: #"SYSTEM_SYSRULES_queryFilter"(
                field: "id",
                operator: "in",
                value: ri!modelVersion[local!fields.relationship][recordType!AIA Model Version Example.id]
              ),
              pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 100)
            ),
            fetchTotalCount: false
          ).data
        ),
        if(
          or(
            not(local!isExtraction),
            a!match(
              value: rule!AIA_ExtractJson(
                json: fv!item[recordType!AIA Model Version Example.rule]
              ),
              whenTrue: or(
                a!isNullOrEmpty(fv!value),
                a!isNullOrEmpty(ri!fields)
              ),
              then: true,
              whenTrue: tostring(index(fv!value,"type",null())) = "FIELD",
              then: contains(
                touniformstring(
                  ri!fields[recordType!AIA Extraction Model Version Field.key]
                ),
                tostring(fv!value.field)
              ),
              default: true
            )
          ),
          fv!item,
          null
        )
      )
    ),
    null
  )
)

