_a-0000ee3e-060c-8000-9bb8-011c48011c48_.sail_code:
a!localVariables(
  local!modelFields: #"SYSTEM_SYSRULES_fromJson_v1"(#"SYSTEM_SYSRULES_toJson_v1"(
    a!defaultValue(
      ri!fields,
      filter(
        rule!AIA_Extraction_DisplayPromptField(
          field: _,
          mode: ri!mode,
          operation: ri!operation
        ),
        ri!ExtractionModelVersion[recordType!AIA Extraction Model Version.fields]
      )
    )
  )),
  if(
    a!isNotNullOrEmpty(local!modelFields),
    a!localVariables(
      local!fields: reduce(
        #"urn:appian:function:v1:a:update",
        a!map(),
        merge(
          local!modelFields.key,
          #"SYSTEM_SYSRULES_forEach"(
            local!modelFields,
            rule!AIA_FieldJsonSchema(
              item: fv!item,
              mode: ri!mode,
              operation: ri!operation,
              includeArrays: a!defaultValue(ri!includeArrays, true)
            )
          )
        )
      ),
      local!map: #"SYSTEM_SYSRULES_fromJson_v1"(
        #"SYSTEM_SYSRULES_toJson_v1"(
          value: a!map(
            "$schema": "http://json-schema.org/draft-07/schema#",
            type: "object",
            properties: local!fields,
            required: reject(
              fn!isnull,
              index(
                local!modelFields.key,
                where(local!modelFields.required)
              )
            )
          ),
          removeNullOrEmptyFields: true
        )
      ),
      if(
        ri!returnJson,
        prettyprintjson(#"SYSTEM_SYSRULES_toJson_v1"(local!map)),
        local!map
      )
    ),
    null
  )
)

